import React, { useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Badge } from 'react-bootstrap';
import { useCartActions, useCartData } from '../../context/CartContext';

const CartComponent = ({ config, data }) => {
  const { cart, loading } = useCartData();
  const { updateCartItem, removeCartItem } = useCartActions();

  // Use cart data from context if available, otherwise use props data
  const cartData = cart || data;
  const { items = [], total = 0, itemCount = 0, subtotal = 0 } = cartData;

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const handleQuantityChange = async (itemId, newQuantity) => {
    if (newQuantity < 1) {
      await removeCartItem(itemId);
    } else {
      await updateCartItem(itemId, newQuantity);
    }
  };

  // Force re-render when cart data changes
  useEffect(() => {
    console.log('Cart data updated:', cartData);
  }, [cartData]);

  const renderCartItem = (item) => (
    <div key={item._id || item.id} className="cart-item mb-4 p-4 bg-white rounded shadow-sm">
      <Row className="align-items-center">
        <Col md={3} lg={2}>
          <div className="position-relative">
            <img
              src={item.image || 'https://via.placeholder.com/100x100'}
              alt={item.name}
              className="img-fluid rounded"
              style={{ width: '100px', height: '100px', objectFit: 'cover' }}
            />
            {item.stock && item.stock <= 2 && (
              <Badge bg="danger" className="position-absolute top-0 start-0">
                {item.stock} LEFT
              </Badge>
            )}
          </div>
        </Col>

        <Col md={6} lg={7}>
          <div className="item-details">
            <h6 className="mb-2 fw-bold">{item.name}</h6>

            {/* Size Dropdown */}
            <div className="mb-2">
              <Form.Select 
                size="sm" 
                className="size-dropdown"
                style={{ width: '120px' }}
                defaultValue={item.attributes?.size || 'M'}
              >
                <option value="S">Size: S</option>
                <option value="M">Size: M</option>
                <option value="L">Size: L</option>
                <option value="XL">Size: XL</option>
              </Form.Select>
            </div>

            {/* Quantity Controls */}
            <div className="d-flex align-items-center mb-2">
              <Button
                variant="outline-secondary"
                size="sm"
                className="quantity-btn"
                onClick={() => handleQuantityChange(item._id || item.id, item.quantity - 1)}
              >
                -
              </Button>
              <span className="mx-3 fw-bold">{item.quantity}</span>
              <Button
                variant="outline-secondary"
                size="sm"
                className="quantity-btn"
                onClick={() => handleQuantityChange(item._id || item.id, item.quantity + 1)}
              >
                +
              </Button>
            </div>

            {/* Stock Warning */}
            {item.stock && item.stock <= 2 && (
              <div className="mb-2">
                <Badge bg="danger" className="small">Only {item.stock} left</Badge>
              </div>
            )}

            {/* Price */}
            <div className="d-flex align-items-center mb-2">
              <span className="fw-bold me-3">{formatCurrency(item.salePrice || item.price)}</span>
              {item.salePrice && item.salePrice < item.price && (
                <span className="text-muted text-decoration-line-through">
                  {formatCurrency(item.price)}
                </span>
              )}
            </div>

            {/* Action Links */}
            <div className="d-flex">
              <button 
                className="btn btn-link text-muted p-0 me-3"
                onClick={() => removeCartItem(item._id || item.id)}
              >
                <i className="fas fa-trash me-1"></i>
                Remove
              </button>
              <button className="btn btn-link text-muted p-0">
                <i className="fas fa-heart me-1"></i>
                Move to Wishlist
              </button>
            </div>
          </div>
        </Col>

        <Col md={3} lg={3} className="text-end">
          <div className="item-price">
            <span className="fw-bold">{formatCurrency((item.salePrice || item.price) * item.quantity)}</span>
          </div>
        </Col>
      </Row>
    </div>
  );

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-component py-5">
      <Container>
        <Row>
          {/* Shopping Bag Section */}
          <Col lg={8}>
            <div className="cart-items-section">
              <h3 className="mb-4 fw-bold">Shopping Bag ({itemCount} items)</h3>

              {/* Free Shipping Banner */}
              <div className="alert alert-success mb-4">
                <i className="fas fa-truck me-2"></i>
                <strong>Yay you got a free shipping!!</strong>
              </div>

              {/* Delivery Info */}
              <div className="mb-4">
                <strong>Delivery to Dubai by Mon, 21st Jul</strong>
              </div>

              {items.length > 0 ? (
                items.map(renderCartItem)
              ) : (
                <div className="text-center py-5">
                  <i className="fas fa-shopping-bag fa-3x text-muted mb-3"></i>
                  <h5 className="text-muted">Your cart is empty</h5>
                  <p className="text-muted">Add some products to get started!</p>
                </div>
              )}
            </div>
          </Col>

          {/* Order Summary Section */}
          <Col lg={4}>
            <Card className="order-summary sticky-top" style={{ top: '20px' }}>
              <Card.Header className="bg-dark text-white">
                <h5 className="mb-0">Order Summary</h5>
              </Card.Header>
              <Card.Body>
                {/* Shukran Loyalty Program */}
                <div className="mb-4">
                  <div className="bg-light p-3 rounded mb-3">
                    <small className="text-muted">
                      Your order could earn you 151 Shukrans, Exclusive Offers And More
                    </small>
                  </div>
                  <div className="text-center p-3 bg-light rounded">
                    <div className="mb-2">
                      <strong>S Shukran</strong>
                    </div>
                    <Button variant="outline-primary" size="sm">
                      Join Now and Enjoy Benefits &gt;
                    </Button>
                  </div>
                </div>

                {/* Coupon Section */}
                <div className="mb-4">
                  <h6 className="mb-2">Apply coupon/Voucher code</h6>
                  <div className="d-flex">
                    <Form.Control
                      type="text"
                      placeholder="Enter Coupon Code"
                      size="sm"
                      className="me-2"
                    />
                    <Button variant="outline-primary" size="sm">
                      Apply
                    </Button>
                  </div>
                </div>

                {/* Payment Options */}
                <div className="mb-4">
                  <div className="mb-2">
                    <small className="text-muted">
                      Split into 4 Interest-Free Monthly Payments of AED 188.25
                    </small>
                  </div>
                  <div className="d-flex align-items-center">
                    <img 
                      src="https://via.placeholder.com/60x30" 
                      alt="tabby" 
                      className="me-2"
                    />
                    <img 
                      src="https://via.placeholder.com/60x30" 
                      alt="tamara" 
                      className="me-2"
                    />
                  </div>
                </div>

                {/* Price Breakdown */}
                <div className="mb-4">
                  <div className="d-flex justify-content-between mb-2">
                    <span>Sub Total:</span>
                    <span>{formatCurrency(subtotal)}</span>
                  </div>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Shipping Fee:</span>
                    <span className="text-success">AED 12 Free</span>
                  </div>
                  <hr />
                  <div className="d-flex justify-content-between mb-3">
                    <strong>Grand Total:</strong>
                    <strong>{formatCurrency(total)}</strong>
                  </div>
                </div>

                <Button variant="dark" className="w-100 mb-2" disabled={items.length === 0}>
                  Proceed to Checkout
                </Button>

                <Button variant="outline-secondary" className="w-100">
                  Continue Shopping
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default CartComponent;
