import React from 'react';
import { Row, Col, Card, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const FeaturedProductsComponent = ({ config, data }) => {
  const {
    title = 'Featured Products',
    products = []
  } = data;

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const renderPrice = (product) => {
    if (product.salePrice && product.salePrice < product.price) {
      const discount = Math.round(((product.price - product.salePrice) / product.price) * 100);
      return (
        <div className="d-flex align-items-center">
          <span className="fw-bold me-2">{formatCurrency(product.salePrice)}</span>
          <span className="text-muted text-decoration-line-through me-2">
            {formatCurrency(product.price)}
          </span>
          <span className="text-success small">{discount}% OFF</span>
        </div>
      );
    }
    return <span className="fw-bold">{formatCurrency(product.price)}</span>;
  };

  const renderProductCard = (product) => (
    <Col key={product.id} className="col-md-6 col-lg-3 mb-4">
      <Link to={`/product/${product.id}`} className="text-decoration-none">
        <Card className="product-card h-100">
          <div className="position-relative">
            <Card.Img
              variant="top"
              src={product.image || 'https://via.placeholder.com/300x400'}
              style={{ height: '300px', objectFit: 'cover' }}
            />

            <div className="position-absolute top-0 end-0 m-2">
              <Badge bg="dark">Featured</Badge>
            </div>

            <div className="position-absolute bottom-0 end-0 m-2">
              <button
                className="btn btn-light rounded-circle"
                style={{ width: '35px', height: '35px' }}
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                }}
              >
                <i className="fas fa-heart text-muted"></i>
              </button>
            </div>
          </div>

          <Card.Body className="d-flex flex-column">
            <div className="mb-2">
              {renderPrice(product)}
            </div>

            <Card.Title className="mb-2" style={{ fontSize: '0.9rem' }}>
              {product.name}
            </Card.Title>

            {product.category && (
              <div className="mb-2">
                <Badge bg="primary" className="small">{product.category}</Badge>
              </div>
            )}
          </Card.Body>
        </Card>
      </Link>
    </Col>
  );

  return (
    <div className="featured-products-section py-5">
      <div className="container">
        <h2 className="text-center mb-5 fw-bold">{title}</h2>

        <Row>
          {products.map(renderProductCard)}
        </Row>

        {products.length === 0 && (
          <div className="text-center py-4">
            <p className="text-muted">No featured products available</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FeaturedProductsComponent;
