import React, { useState, useEffect, memo } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Container, 
  Row, 
  Col, 
  Button, 
  Badge, 
  Form,
  Alert,
  Spinner
} from 'react-bootstrap';
import { useCartActions } from '../context/CartContext';
import axios from 'axios';

const ProductDetail = memo(() => {
  const { productId } = useParams();
  const { addToCart } = useCartActions();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedColor, setSelectedColor] = useState('');
  const [selectedSize, setSelectedSize] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [addingToCart, setAddingToCart] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Fetch product data from API
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/products/${productId}`);

        if (response.data && response.data.success) {
          const productData = response.data.product;
          setProduct(productData);

          // Set default selected color if colors exist
          if (productData.colors && productData.colors.length > 0) {
            setSelectedColor(productData.colors[0].name);
          }
        } else {
          setError('Failed to load product');
        }
      } catch (error) {
        console.error('Error fetching product:', error);
        setError('Failed to load product. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    if (productId) {
      fetchProduct();
    }
  }, [productId]);

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(0)}`;
  };

  const handleAddToCart = async () => {
    // Check if size selection is required and not selected
    if (product.sizes && product.sizes.length > 0 && !selectedSize) {
      alert('Please select a size');
      return;
    }

    setAddingToCart(true);
    try {
      const result = await addToCart(product._id, quantity);
      if (result.success) {
        console.log('Product added to cart successfully!');
      } else {
        console.error('Error adding to cart:', result.error);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
    } finally {
      setAddingToCart(false);
    }
  };

  const handleColorSelect = (color) => {
    setSelectedColor(color.name);
    // Update product price based on color
    const colorVariant = product.colors.find(c => c.name === color.name);
    if (colorVariant) {
      setProduct({
        ...product,
        price: colorVariant.price,
        salePrice: colorVariant.salePrice || Math.round(colorVariant.price * 0.5) // 50% off
      });
    }
  };

  const copyCouponCode = () => {
    navigator.clipboard.writeText('NEW50');
    alert('Coupon code copied!');
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
        <Spinner animation="border" variant="primary" />
      </div>
    );
  }

  if (error || !product) {
    return (
      <Container className="py-5">
        <Alert variant="danger">
          {error || 'Product not found'}
        </Alert>
      </Container>
    );
  }

  const discount = product.originalPrice ? Math.round(((product.originalPrice - product.salePrice) / product.originalPrice) * 100) : 0;
  const installmentAmount = product.salePrice ? (product.salePrice / 4).toFixed(2) : 0;

  return (
    <div className="bg-light min-vh-100">
      {/* Breadcrumbs */}
      <div className="bg-white border-bottom">
        <Container className="py-2">
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb mb-0">
              <li className="breadcrumb-item">
                <Link to="/" className="text-decoration-none">Home</Link>
              </li>
              <li className="breadcrumb-item">
                <Link to="/men" className="text-decoration-none">Men</Link>
              </li>
              <li className="breadcrumb-item">
                <Link to="/men/shorts" className="text-decoration-none">Shorts</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                {product.name}
              </li>
            </ol>
          </nav>
        </Container>
      </div>

      <Container className="py-4">
        <Row>
          {/* Product Images */}
          <Col lg={8} className="mb-4">
            <div className="bg-white rounded shadow-sm p-4">
              <div className="row">
                <Col md={6}>
                  <img
                    src={product.images[0]}
                    alt={product.name}
                    className="img-fluid rounded"
                    style={{ height: '500px', objectFit: 'cover' }}
                  />
                </Col>
                <Col md={6}>
                  <img
                    src={product.images[1]}
                    alt={product.name}
                    className="img-fluid rounded"
                    style={{ height: '500px', objectFit: 'cover' }}
                  />
                </Col>
              </div>
            </div>
          </Col>

                     {/* Product Details */}
           <Col lg={4}>
             <div className="bg-white rounded shadow-sm p-4 product-detail-card">
              {/* Bestseller Badge */}
              {product.isBestseller && (
                <Badge bg="dark" className="mb-3">Bestseller</Badge>
              )}

              {/* Brand and Product Name */}
              <h4 className="fw-bold mb-1">{product.brand}</h4>
              <h5 className="text-muted mb-3">{product.name}</h5>

                             {/* Price */}
               <div className="mb-3">
                 {product.originalPrice && product.originalPrice > product.salePrice ? (
                   <>
                     <span className="text-muted text-decoration-line-through me-2">
                       {formatCurrency(product.originalPrice)}
                     </span>
                     <span className="fw-bold fs-4">{formatCurrency(product.salePrice)}</span>
                     <span className="text-success ms-2">Save {formatCurrency(product.originalPrice - product.salePrice)}</span>
                   </>
                 ) : (
                   <span className="fw-bold fs-4">{formatCurrency(product.price)}</span>
                 )}
               </div>

                             {/* Loyalty Program */}
               <div className="mb-3 p-3 loyalty-info rounded">
                 <small className="text-muted">
                   Earn {product.loyaltyPoints} Shukrans + 2 Tier Bonuses
                 </small>
               </div>

               {/* Payment Options */}
               <div className="mb-3 p-3 installment-info rounded">
                <div className="d-flex align-items-center mb-2">
                  <small className="text-muted">
                    Pay {formatCurrency(installmentAmount)} now and the rest in 3 payments over time. 
                    No hidden fees, no interest!
                  </small>
                </div>
                <div className="d-flex align-items-center">
                  <img src="https://via.placeholder.com/60x20/000000/ffffff?text=tamara" alt="tamara" className="me-2" />
                  <img src="https://via.placeholder.com/60x20/000000/ffffff?text=tabby" alt="tabby" />
                </div>
              </div>

              {/* Discount Offer */}
              <div className="mb-3 p-3 bg-warning bg-opacity-10 rounded">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <strong>Get extra 50% off* your first order</strong>
                    <br />
                    <small className="text-muted">Buy it for {formatCurrency(product.salePrice)}</small>
                  </div>
                                     <Button 
                     variant="outline-dark" 
                     size="sm"
                     onClick={copyCouponCode}
                     className="coupon-button"
                   >
                     NEW50
                     <br />
                     <small>Tap to copy</small>
                   </Button>
                </div>
              </div>

              {/* Delivery Info */}
              <div className="mb-3">
                <small className="text-muted">
                  Delivery by {product.deliveryDate} â€¢ {product.location}
                </small>
              </div>

                             {/* Color Selection */}
               {product.colors && product.colors.length > 0 && (
                 <div className="mb-3">
                   <div className="d-flex justify-content-between align-items-center mb-2">
                     <span className="fw-bold">Selected Color: {selectedColor}</span>
                   </div>
                   <div className="d-flex gap-2">
                     {product.colors.map((color) => (
                       <div
                         key={color.name}
                         className={`border rounded p-2 color-option ${selectedColor === color.name ? 'selected' : 'border-light'}`}
                         onClick={() => handleColorSelect(color)}
                       >
                         <img
                           src={color.image}
                           alt={color.name}
                           className="rounded"
                           style={{ width: '60px', height: '60px', objectFit: 'cover' }}
                         />
                         <div className="text-center mt-1">
                           <small className="fw-bold">{color.name}</small>
                           <br />
                           <small className="text-muted">{formatCurrency(color.price)}</small>
                         </div>
                       </div>
                     ))}
                   </div>
                 </div>
               )}

                             {/* Size Selection */}
               {product.sizes && product.sizes.length > 0 && (
                 <div className="mb-3">
                   <div className="d-flex justify-content-between align-items-center mb-2">
                     <span className="fw-bold">Pick your size</span>
                     <Link to="/size-guide" className="text-decoration-none small">Size Guide</Link>
                   </div>
                   <div className="d-flex gap-2 flex-wrap">
                     {product.sizes.map((size) => (
                       <Button
                         key={size.name}
                         variant={selectedSize === size.name ? 'dark' : 'outline-dark'}
                         size="sm"
                         disabled={size.stock === 0}
                         onClick={() => setSelectedSize(size.name)}
                         className="px-3 py-2 size-button"
                       >
                         {size.name}
                         {size.stock > 0 && (
                           <div className="small text-muted">{size.stock} left</div>
                         )}
                       </Button>
                     ))}
                   </div>
                 </div>
               )}

              {/* Add to Bag Button */}
              <div className="d-flex gap-2 mb-3">
                                 <Button
                   variant="dark"
                   size="lg"
                   className="flex-grow-1"
                   onClick={handleAddToCart}
                   disabled={addingToCart || (product.sizes && product.sizes.length > 0 && !selectedSize)}
                 >
                  {addingToCart ? (
                    <>
                      <Spinner size="sm" className="me-2" />
                      Adding...
                    </>
                  ) : (
                    'Add to Bag'
                  )}
                </Button>
                <Button variant="outline-dark" size="lg">
                  <i className="fas fa-heart"></i>
                </Button>
              </div>

              {/* Product Information */}
              <div className="border-top pt-3">
                <h6 className="fw-bold">Product Information</h6>
                <p className="text-muted small mb-0">{product.description}</p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
});

export default ProductDetail;
