import React from 'react';
import { Alert, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const BannerComponent = ({ config, data }) => {
  const {
    title = 'Special Offer',
    message = 'Get 50% off on selected items',
    backgroundColor = '#ff6b6b',
    textColor = '#ffffff',
    image,
    link,
    showTimer = false,
    endDate
  } = data;

  return (
    <div 
      className="banner-section py-4"
      style={{
        backgroundColor,
        color: textColor
      }}
    >
      <div className="container">
        <div className="row align-items-center">
          <div className="col-md-8">
            <div className="banner-content">
              <h3 className="fw-bold mb-2">{title}</h3>
              <p className="mb-3">{message}</p>

              {showTimer && endDate && (
                <div className="countdown-timer mb-3">
                  <small>Offer ends in: 2 days 14 hours 30 minutes</small>
                </div>
              )}

              {link && (
                <Link to={link}>
                  <Button variant="light" className="fw-bold">
                    Shop Now
                  </Button>
                </Link>
              )}
            </div>
          </div>

          {image && (
            <div className="col-md-4">
              <div className="banner-image text-center">
                <img
                  src={image}
                  alt={title}
                  className="img-fluid rounded"
                  style={{ maxHeight: '200px' }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default BannerComponent;
