import React from 'react';
import { useParams } from 'react-router-dom';
import { Spinner, Alert, Button } from 'react-bootstrap';
import useServerDrivenUI from '../hooks/useServerDrivenUI';
import HeroComponent from './ui/HeroComponent';
import ProductGridComponent from './ui/ProductGridComponent';
import BannerComponent from './ui/BannerComponent';
import CategoryListComponent from './ui/CategoryListComponent';
import FeaturedProductsComponent from './ui/FeaturedProductsComponent';
import CartComponent from './ui/CartComponent';
import CheckoutComponent from './ui/CheckoutComponent';
import HeaderComponent from './ui/HeaderComponent';
import FooterComponent from './ui/FooterComponent';

const DynamicLayout = ({ pageType: propPageType, pageId: propPageId }) => {
  const params = useParams();
  const pageType = propPageType || params.pageType;
  const pageId = propPageId || params.pageId;

  console.log('ðŸŽ¯ DynamicLayout props:', { propPageType, propPageId });
  console.log('ðŸŽ¯ DynamicLayout params:', params);
  console.log('ðŸŽ¯ DynamicLayout final:', { pageType, pageId });

  // Test API call directly
  React.useEffect(() => {
    const testAPI = async () => {
      try {
        console.log('ðŸ§ª Testing API call...');
        const response = await fetch(`http://localhost:3000/api/health`);
        const data = await response.json();
        console.log('ðŸ§ª API test result:', data);
      } catch (error) {
        console.error('ðŸ§ª API test failed:', error);
      }
    };
    testAPI();
  }, []);

  // User context - in a real app, this would come from auth context
  const userContext = {
    userId: '60d21b4667d0d8992e610c85',
    userRole: 'customer',
    deviceType: 'desktop',
    locale: 'en-US',
    timezone: 'Asia/Dubai'
  };

  const {
    layout,
    loading,
    error,
    refreshLayout,
    updateComponentData
  } = useServerDrivenUI(pageType, pageId, userContext);

  const renderComponent = (component) => {
    const { type, config, data } = component;

    switch (type) {
      case 'hero':
        return <HeroComponent config={config} data={data} />;
      case 'productGrid':
        return <ProductGridComponent config={config} data={data} />;
      case 'banner':
        return <BannerComponent config={config} data={data} />;
      case 'categoryList':
        return <CategoryListComponent config={config} data={data} />;
      case 'featuredProducts':
        return <FeaturedProductsComponent config={config} data={data} />;
      case 'cart':
        return <CartComponent config={config} data={data} />;
      case 'checkout':
        return <CheckoutComponent config={config} data={data} />;
      case 'header':
        return <HeaderComponent config={config} data={data} />;
      case 'footer':
        return <FooterComponent config={config} data={data} />;
      default:
        return (
          <div className="alert alert-warning">
            Unknown component type: {type}
          </div>
        );
    }
  };

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Loading dynamic layout...</p>
        <Button 
          variant="outline-primary" 
          onClick={() => {
            console.log('ðŸ§ª Manual API test...');
            fetch('http://localhost:3000/api/health')
              .then(res => res.json())
              .then(data => console.log('ðŸ§ª Manual test result:', data))
              .catch(err => console.error('ðŸ§ª Manual test error:', err));
          }}
          className="mt-3"
        >
          Test API Connection
        </Button>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container py-5">
        <Alert variant="danger">
          <Alert.Heading>Error Loading Layout</Alert.Heading>
          <p>{error}</p>
          <hr />
          <Button variant="outline-danger" onClick={refreshLayout}>
            Try Again
          </Button>
        </Alert>
      </div>
    );
  }

  if (!layout) {
    return (
      <div className="container py-5">
        <Alert variant="warning">
          <Alert.Heading>No Layout Found</Alert.Heading>
          <p>No layout configuration found for this page.</p>
        </Alert>
      </div>
    );
  }

  return (
    <div className="dynamic-layout">
      {/* Page Metadata */}
      {layout.metadata && (
        <div style={{ display: 'none' }}>
          <title>{layout.metadata.title}</title>
          <meta name="description" content={layout.metadata.description} />
          {layout.metadata.keywords && (
            <meta name="keywords" content={layout.metadata.keywords.join(', ')} />
          )}
          {layout.metadata.ogImage && (
            <meta property="og:image" content={layout.metadata.ogImage} />
          )}
        </div>
      )}

      {/* Render Components */}
      {layout.components.map((component, index) => (
        <div key={`${component.id}-${index}`} className="component-wrapper">
          {renderComponent(component)}
        </div>
      ))}
    </div>
  );
};

export default DynamicLayout;
