import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useCartActions } from '../../context/CartContext';

const CartComponent = ({ config, data }) => {
  const { items = [], total = 0, itemCount = 0, subtotal = 0 } = data;
  const { updateCartItem, removeCartItem } = useCartActions();

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const handleQuantityChange = async (itemId, newQuantity) => {
    if (newQuantity < 1) {
      await removeCartItem(itemId);
    } else {
      await updateCartItem(itemId, newQuantity);
    }
  };

  const renderCartItem = (item) => (
    <div key={item.id} className="cart-item mb-4 p-4 bg-white rounded shadow-sm">
      <Row className="align-items-center">
        <Col md={3} lg={2}>
          <div className="position-relative">
            <img
              src={item.image || 'https://via.placeholder.com/100x100'}
              alt={item.name}
              className="img-fluid rounded"
              style={{ width: '100px', height: '100px', objectFit: 'cover' }}
            />
            <button
              className="btn btn-light rounded-circle position-absolute top-0 end-0 m-2"
              style={{ width: '32px', height: '32px', fontSize: '0.8rem' }}
            >
              <i className="fas fa-heart text-muted"></i>
            </button>
          </div>
        </Col>

        <Col md={6} lg={7}>
          <div className="item-details">
            <h6 className="mb-2 fw-bold">{item.name}</h6>
            <p className="text-muted mb-2">Size: {item.attributes?.size || 'M'}</p>
            <div className="d-flex align-items-center mb-2">
              <span className="fw-bold me-3">{formatCurrency(item.salePrice || item.price)}</span>
              {item.salePrice && item.salePrice < item.price && (
                <span className="text-muted text-decoration-line-through">
                  {formatCurrency(item.price)}
                </span>
              )}
            </div>
          </div>
        </Col>

        <Col md={3} lg={3}>
          <div className="quantity-control d-flex align-items-center">
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
            >
              -
            </Button>
            <span className="mx-3 fw-bold">{item.quantity}</span>
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
            >
              +
            </Button>
          </div>
        </Col>
      </Row>
    </div>
  );

  return (
    <div className="cart-component py-5">
      <Container>
        <Row>
          <Col lg={8}>
            <div className="cart-items-section">
              <h3 className="mb-4 fw-bold">Shopping Bag ({itemCount} items)</h3>

              {items.length > 0 ? (
                items.map(renderCartItem)
              ) : (
                <div className="text-center py-5">
                  <i className="fas fa-shopping-bag fa-3x text-muted mb-3"></i>
                  <h5 className="text-muted">Your cart is empty</h5>
                  <p className="text-muted">Add some products to get started!</p>
                </div>
              )}
            </div>
          </Col>

          <Col lg={4}>
            <Card className="order-summary sticky-top" style={{ top: '20px' }}>
              <Card.Header className="bg-primary text-white">
                <h5 className="mb-0">Order Summary</h5>
              </Card.Header>
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <span>Subtotal:</span>
                  <span>{formatCurrency(subtotal)}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Shipping:</span>
                  <span>AED 12.00</span>
                </div>
                <hr />
                <div className="d-flex justify-content-between mb-3">
                  <strong>Total:</strong>
                  <strong>{formatCurrency(total + 12)}</strong>
                </div>

                <Button variant="primary" className="w-100 mb-2" disabled={items.length === 0}>
                  Proceed to Checkout
                </Button>

                <Button variant="outline-secondary" className="w-100">
                  Continue Shopping
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default CartComponent;
