import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';

const ProductSkeleton = ({ count = 8 }) => {
  const skeletons = Array.from({ length: count }, (_, index) => (
    <Col key={index} className="col-md-3 mb-4">
      <Card className="product-card h-100 border-0 shadow-sm">
        <div className="position-relative">
          {/* Image skeleton */}
          <div 
            className="skeleton-image"
            style={{ 
              height: '400px', 
              backgroundColor: '#f0f0f0',
              borderRadius: '8px 8px 0 0'
            }}
          />

          {/* Badge skeleton */}
          <div 
            className="position-absolute top-0 start-0 m-2"
            style={{
              width: '80px',
              height: '24px',
              backgroundColor: '#e0e0e0',
              borderRadius: '4px'
            }}
          />

          {/* Wishlist button skeleton */}
          <div 
            className="position-absolute bottom-0 end-0 m-2"
            style={{
              width: '40px',
              height: '40px',
              backgroundColor: '#e0e0e0',
              borderRadius: '50%'
            }}
          />
        </div>

        <Card.Body className="d-flex flex-column p-3">
          {/* Price skeleton */}
          <div className="mb-2">
            <div 
              style={{
                width: '60px',
                height: '20px',
                backgroundColor: '#e0e0e0',
                borderRadius: '4px',
                marginBottom: '8px'
              }}
            />
          </div>

          {/* Title skeleton */}
          <div className="mb-2">
            <div 
              style={{
                width: '100%',
                height: '16px',
                backgroundColor: '#e0e0e0',
                borderRadius: '4px',
                marginBottom: '4px'
              }}
            />
            <div 
              style={{
                width: '80%',
                height: '16px',
                backgroundColor: '#e0e0e0',
                borderRadius: '4px'
              }}
            />
          </div>

          {/* Badge skeleton */}
          <div className="mb-2">
            <div 
              style={{
                width: '100px',
                height: '20px',
                backgroundColor: '#e0e0e0',
                borderRadius: '4px'
              }}
            />
          </div>
        </Card.Body>
      </Card>
    </Col>
  ));

  return (
    <Row>
      {skeletons}
    </Row>
  );
};

export default ProductSkeleton;
