import React, { useState, useCallback, memo } from 'react';
import { Link } from 'react-router-dom';
import { Spinner, Card, Button, Form, InputGroup, Alert, Badge } from 'react-bootstrap';
import CartItem from './CartItem';
import EmptyCart from './EmptyCart';
import { useCartData, useCartActions } from '../context/CartContext';

const Cart = memo(() => {
  const { cart, loading, error } = useCartData();
  const { 
    updateCartItem, 
    removeCartItem, 
    applyCoupon, 
    removeCoupon, 
    setError 
  } = useCartActions();

  const [couponCode, setCouponCode] = useState('');
  const [couponError, setCouponError] = useState('');
  const [couponSuccess, setCouponSuccess] = useState('');
  const [itemLoading, setItemLoading] = useState({}); // { [itemId]: true/false }

  // Update cart item quantity
  const handleQuantityUpdate = useCallback(async (itemId, quantity) => {
    setItemLoading(prev => ({ ...prev, [itemId]: true }));
    try {
      const result = await updateCartItem(itemId, quantity);
      if (!result.success) {
        setError(result.error);
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      setError('An error occurred while updating the quantity');
    } finally {
      setItemLoading(prev => ({ ...prev, [itemId]: false }));
    }
  }, [updateCartItem, setError]);

  // Remove cart item
  const handleRemoveItem = useCallback(async (itemId) => {
    setItemLoading(prev => ({ ...prev, [itemId]: true }));
    try {
      const result = await removeCartItem(itemId);
      if (!result.success) {
        setError(result.error);
      }
    } catch (error) {
      console.error('Error removing item:', error);
      setError('An error occurred while removing the item');
    } finally {
      setItemLoading(prev => ({ ...prev, [itemId]: false }));
    }
  }, [removeCartItem, setError]);

  // Handle coupon application
  const handleApplyCoupon = useCallback(async (e) => {
    e.preventDefault();
    if (!couponCode.trim()) {
      setCouponError('Please enter a coupon code');
      return;
    }
    setCouponError('');
    setCouponSuccess('');
    try {
      const result = await applyCoupon(couponCode);
      if (result.success) {
        setCouponSuccess('Hurray! Coupon applied successfully');
        setCouponCode('');
      } else {
        setCouponError(result.error);
      }
    } catch (error) {
      console.error('Error applying coupon:', error);
      setCouponError('An error occurred while applying the coupon');
    }
  }, [couponCode, applyCoupon]);

  // Handle coupon removal
  const handleRemoveCoupon = useCallback(async (code) => {
    try {
      const result = await removeCoupon(code);
      if (!result.success) {
        setError(result.error);
      }
    } catch (error) {
      console.error('Error removing coupon:', error);
      setError('An error occurred while removing the coupon');
    }
  }, [removeCoupon, setError]);

  // Format price as currency
  const formatCurrency = useCallback((amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  }, []);

  // Check if cart is empty
  const isCartEmpty = !cart || !cart.items || cart.items.length === 0;

  // Calculate totals
  const subtotal = cart?.subtotal || 0;
  const shippingFee = 12;
  const couponDiscount = 40.53;
  const grandTotal = subtotal + shippingFee - couponDiscount;
  const savings = couponDiscount;

  return (
    <main className="container py-5">
      {/* Only show global loading overlay for initial load or coupon actions */}
      {loading && (
        <div className="loading-overlay">
          <Spinner animation="border" variant="primary" />
        </div>
      )}
      {/* Error message */}
      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}
      {isCartEmpty ? (
        <EmptyCart />
      ) : (
        <div className="row">
          {/* Shopping Bag Section */}
          <div className="col-lg-8">
            <div className="shopping-bag-section">
              {/* Header */}
              <h2 className="mb-4 fw-bold">Shopping Bag</h2>

              {/* Shipping Banner */}
              <div className="shipping-banner mb-4">
                <div className="d-flex align-items-center justify-content-between">
                  <div className="d-flex align-items-center">
                    <i className="fas fa-truck text-success me-2"></i>
                    <span className="text-success fw-bold">Yay you got a free shipping!!</span>
                  </div>
                  <div className="delivery-info">
                    <span className="text-muted">Delivery to Dubai by Mon, 21st Jul</span>
                  </div>
                </div>
              </div>

              {/* Cart Items */}
              <div className="cart-items">
                {cart.items.map(item => (
                  <CartItem
                    key={item._id}
                    item={item}
                    onUpdateQuantity={handleQuantityUpdate}
                    onRemove={handleRemoveItem}
                    formatCurrency={formatCurrency}
                    loading={!!itemLoading[item._id]}
                  />
                ))}
              </div>
            </div>
          </div>

          {/* Order Summary Section */}
          <div className="col-lg-4">
            <div className="order-summary-section">
              <h3 className="mb-4 fw-bold">Order Summary</h3>

              {/* Shukran Rewards */}
              <div className="shukran-rewards mb-4">
                <p className="mb-3">Your order could earn you 23 Shukrans, Exclusive Offers And More</p>
                <div className="shukran-banner p-3 rounded">
                  <div className="d-flex align-items-center justify-content-between">
                    <div className="d-flex align-items-center">
                      <div className="shukran-logo me-3">
                        <span className="fw-bold fs-4">S</span>
                        <span className="text-warning">Shukran</span>
                      </div>
                      <div className="shukran-icons">
                        <i className="fas fa-star text-warning me-1"></i>
                        <i className="fas fa-crown text-warning"></i>
                      </div>
                    </div>
                    <Button variant="outline-dark" size="sm">
                      Join Now and Enjoy Benefits &gt;
                    </Button>
                  </div>
                </div>
              </div>

              {/* Coupon Application */}
              <div className="coupon-section mb-4">
                {couponSuccess && (
                  <div className="coupon-success mb-3">
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="text-success">{couponSuccess}</span>
                      <Button
                        variant="link"
                        className="text-danger p-0"
                        onClick={() => handleRemoveCoupon('NEW50')}
                        disabled={loading}
                      >
                        Remove
                      </Button>
                    </div>
                    <div className="applied-coupon mt-2">
                      <Badge bg="success" className="me-2">NEW50</Badge>
                    </div>
                  </div>
                )}

                {!couponSuccess && (
                  <Form onSubmit={handleApplyCoupon} className="mb-3">
                    <InputGroup>
                      <Form.Control
                        type="text"
                        placeholder="Enter coupon code"
                        value={couponCode}
                        onChange={(e) => setCouponCode(e.target.value)}
                      />
                      <Button variant="outline-primary" type="submit" disabled={loading}>Apply</Button>
                    </InputGroup>
                    {couponError && <Form.Text className="text-danger">{couponError}</Form.Text>}
                  </Form>
                )}
              </div>

              {/* Payment Options */}
              <div className="payment-options mb-4">
                <p className="mb-2">Split into 4 Interest-Free Monthly Payments of AED 28.72</p>
                <div className="d-flex align-items-center">
                  <div className="payment-logo me-3">
                    <span className="text-success fw-bold">tabby</span>
                  </div>
                  <div className="payment-logo">
                    <span className="text-purple fw-bold">tamara</span>
                  </div>
                </div>
              </div>

              {/* Price Breakdown */}
              <div className="price-breakdown mb-4">
                <div className="d-flex justify-content-between mb-2">
                  <span>Sub Total</span>
                  <span>{formatCurrency(subtotal)}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Shipping Fee</span>
                  <span>
                    <span className="text-decoration-line-through text-muted me-2">{formatCurrency(shippingFee)}</span>
                    <span className="text-success">Free</span>
                  </span>
                </div>
                {couponSuccess && (
                  <div className="d-flex justify-content-between mb-2">
                    <span>Coupon Code (NEW50)</span>
                    <span className="text-success">- {formatCurrency(couponDiscount)}</span>
                  </div>
                )}
                <hr />
                <div className="d-flex justify-content-between mb-3">
                  <strong className="fs-5">Grand Total</strong>
                  <strong className="fs-5">{formatCurrency(grandTotal)}</strong>
                </div>

                {/* Savings Message */}
                <div className="savings-message p-3 rounded">
                  <div className="d-flex align-items-center">
                    <i className="fas fa-shopping-bag text-success me-2"></i>
                    <span className="text-success fw-bold">
                      Congrats! You saved {formatCurrency(savings)} in this order
                    </span>
                  </div>
                </div>
              </div>

              {/* Checkout Button */}
              <div className="d-grid">
                <Button variant="dark" size="lg" className="checkout-btn">
                  Proceed to Checkout
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}
    </main>
  );
});

export default Cart;
