import React, { useState, useEffect } from 'react';
import { useCart } from '../../context/CartContext';

const CartComponent = ({ config, data }) => {
  const { cart, updateQuantity, removeItem, applyCoupon, removeCoupon, getAvailableCoupons, loading, error, clearError } = useCart();

  const [couponCode, setCouponCode] = useState('');
  const [availableCoupons, setAvailableCoupons] = useState([]);
  const [showAvailableCoupons, setShowAvailableCoupons] = useState(false);

  useEffect(() => {
    if (cart.items.length > 0) {
      loadAvailableCoupons();
    }
  }, [cart.items]);

  const loadAvailableCoupons = async () => {
    const result = await getAvailableCoupons();
    if (result.success) {
      setAvailableCoupons(result.coupons);
    }
  };

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const handleApplyCoupon = async () => {
    if (!couponCode.trim()) return;

    clearError();
    const result = await applyCoupon(couponCode.trim());
    if (result.success) {
      setCouponCode('');
    }
  };

  const handleRemoveCoupon = async (code) => {
    clearError();
    await removeCoupon(code);
  };

  // Handle different image field formats
  const getItemImage = (item) => {
    if (item.image) return item.image;
    if (item.images && item.images.length > 0) return item.images[0];
    return 'https://via.placeholder.com/100x100';
  };

  if (cart.items.length === 0) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <h2>Your cart is empty</h2>
          <p className="text-muted">Add some products to get started!</p>
          <a href="/" className="btn btn-primary">Continue Shopping</a>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <h2 className="mb-4">Shopping Cart</h2>

      {error && (
        <div className="alert alert-danger alert-dismissible fade show" role="alert">
          {error}
          <button type="button" className="btn-close" onClick={clearError}></button>
        </div>
      )}

      <div className="row">
        {/* Cart Items */}
        <div className="col-lg-8">
          <div className="card">
            <div className="card-body">
              {cart.items.map((item) => (
                <div key={item._id} className="d-flex border-bottom py-3">
                  <img
                    src={getItemImage(item)}
                    alt={item.name}
                    className="rounded me-3"
                    style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                  />

                  <div className="flex-grow-1">
                    <h6 className="mb-1">{item.name}</h6>
                    <p className="text-muted small mb-2">
                      {item.attributes?.size && `Size: ${item.attributes.size}`}
                    </p>

                    <div className="d-flex justify-content-between align-items-center">
                      <div className="d-flex align-items-center">
                        <button
                          className="btn btn-sm btn-outline-secondary"
                          onClick={() => updateQuantity(item._id, item.quantity - 1)}
                          disabled={item.quantity <= 1}
                        >
                          -
                        </button>
                        <span className="mx-3">{item.quantity}</span>
                        <button
                          className="btn btn-sm btn-outline-secondary"
                          onClick={() => updateQuantity(item._id, item.quantity + 1)}
                        >
                          +
                        </button>
                      </div>

                      <div className="text-end">
                        <div className="fw-bold">
                          {formatCurrency((item.salePrice || item.price) * item.quantity)}
                        </div>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => removeItem(item._id)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Order Summary */}
        <div className="col-lg-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Order Summary</h5>
            </div>
            <div className="card-body">
              {/* Available Coupons */}
              <div className="mb-4">
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <h6 className="mb-0">Available Coupons</h6>
                  <button
                    className="btn btn-sm btn-link"
                    onClick={() => setShowAvailableCoupons(!showAvailableCoupons)}
                  >
                    {showAvailableCoupons ? 'Hide' : 'Show'}
                  </button>
                </div>

                {showAvailableCoupons && availableCoupons.length > 0 && (
                  <div className="mb-3">
                    {availableCoupons.map((coupon) => (
                      <div key={coupon.id} className="border rounded p-2 mb-2">
                        <div className="d-flex justify-content-between align-items-center">
                          <div>
                            <strong>{coupon.code}</strong>
                            <small className="d-block text-muted">{coupon.description}</small>
                          </div>
                          <button
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => {
                              setCouponCode(coupon.code);
                              handleApplyCoupon();
                            }}
                          >
                            Apply
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Coupon Input */}
              <div className="mb-4">
                <h6>Apply Coupon</h6>
                <div className="d-flex">
                  <input
                    type="text"
                    className="form-control me-2"
                    placeholder="Enter coupon code"
                    value={couponCode}
                    onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                    onKeyPress={(e) => e.key === 'Enter' && handleApplyCoupon()}
                  />
                  <button
                    className="btn btn-outline-primary"
                    onClick={handleApplyCoupon}
                    disabled={!couponCode.trim()}
                  >
                    Apply
                  </button>
                </div>
              </div>

              {/* Applied Coupons */}
              {cart.appliedCoupons && cart.appliedCoupons.length > 0 && (
                <div className="mb-4">
                  <h6>Applied Coupons</h6>
                  {cart.appliedCoupons.map((coupon, index) => (
                    <div key={index} className="border rounded p-2 mb-2 bg-light">
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <strong>{coupon.code}</strong>
                          <small className="d-block text-success">
                            -{formatCurrency(coupon.discountAmount)}
                          </small>
                        </div>
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleRemoveCoupon(coupon.code)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* Price Breakdown */}
              <div className="border-top pt-3">
                <div className="d-flex justify-content-between mb-2">
                  <span>Subtotal:</span>
                  <span>{formatCurrency(cart.subtotal)}</span>
                </div>

                {cart.totalDiscount > 0 && (
                  <div className="d-flex justify-content-between mb-2 text-success">
                    <span>Discount:</span>
                    <span>-{formatCurrency(cart.totalDiscount)}</span>
                  </div>
                )}

                <hr />
                <div className="d-flex justify-content-between mb-3">
                  <strong>Total:</strong>
                  <strong>{formatCurrency(cart.total)}</strong>
                </div>
              </div>

              {/* Checkout Button */}
              <button className="btn btn-primary w-100" disabled={loading}>
                {loading ? 'Processing...' : 'Proceed to Checkout'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartComponent;
