import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Badge } from 'react-bootstrap';
import { useCartData, useCartActions } from '../../context/CartContext';

const CartComponent = ({ data }) => {
  const { cart, loading, error, couponError, couponSuccess } = useCartData();
  const { 
    updateCartItem, 
    removeCartItem, 
    validateCoupon, 
    applyCoupon, 
    removeCoupon, 
    getAvailableCoupons,
    clearCouponMessages 
  } = useCartActions();

  const [couponCode, setCouponCode] = useState('');
  const [availableCoupons, setAvailableCoupons] = useState([]);
  const [showAvailableCoupons, setShowAvailableCoupons] = useState(false);

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const handleQuantityChange = async (itemId, newQuantity) => {
    if (newQuantity < 1) return;

    const result = await updateCartItem(itemId, newQuantity);
    if (!result.success) {
      console.error('Failed to update quantity:', result.error);
    }
  };

  const handleRemoveItem = async (itemId) => {
    const result = await removeCartItem(itemId);
    if (!result.success) {
      console.error('Failed to remove item:', result.error);
    }
  };

  const handleCouponValidation = async () => {
    if (!couponCode.trim()) return;

    const result = await validateCoupon(couponCode.trim());
    if (result.success) {
      // Auto-apply the coupon if validation is successful
      await handleApplyCoupon();
    }
  };

  const handleApplyCoupon = async () => {
    if (!couponCode.trim()) return;

    const result = await applyCoupon(couponCode.trim());
    if (result.success) {
      setCouponCode('');
    }
  };

  const handleRemoveCoupon = async (code) => {
    const result = await removeCoupon(code);
    if (!result.success) {
      console.error('Failed to remove coupon:', result.error);
    }
  };

  const handleGetAvailableCoupons = async () => {
    const result = await getAvailableCoupons();
    if (result.success) {
      setAvailableCoupons(result.coupons);
      setShowAvailableCoupons(true);
    }
  };

  const handleCopyCoupon = (code) => {
    navigator.clipboard.writeText(code);
    // You could add a toast notification here
  };

  // Clear coupon messages after 5 seconds
  useEffect(() => {
    if (couponError || couponSuccess) {
      const timer = setTimeout(() => {
        clearCouponMessages();
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [couponError, couponSuccess, clearCouponMessages]);

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  if (!cart || cart.items.length === 0) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <h2>Your cart is empty</h2>
          <p className="text-muted">Add some products to get started!</p>
          <Button href="/" variant="primary">Continue Shopping</Button>
        </div>
      </Container>
    );
  }

  return (
    <div className="cart-page">
      <Container className="py-5">
        <Row>
          <Col lg={8}>
            <h2 className="mb-4">Shopping Cart</h2>

            {/* Coupon Messages */}
            {couponError && (
              <Alert variant="danger" dismissible onClose={clearCouponMessages}>
                {couponError}
              </Alert>
            )}

            {couponSuccess && (
              <Alert variant="success" dismissible onClose={clearCouponMessages}>
                {couponSuccess}
              </Alert>
            )}

            {/* Cart Items */}
            {cart.items.map((item) => (
              <Card key={item._id} className="cart-item mb-3">
                <Row className="g-0">
                  <Col md={3}>
                    <img
                      src={item.image || 'https://via.placeholder.com/150x200'}
                      alt={item.name}
                      className="img-fluid rounded-start"
                      style={{ height: '200px', objectFit: 'cover' }}
                    />
                  </Col>
                  <Col md={9}>
                    <Card.Body>
                      <div className="d-flex justify-content-between align-items-start">
                        <div>
                          <h5 className="card-title">{item.name}</h5>
                          <p className="text-muted mb-2">
                            {item.attributes?.size && `Size: ${item.attributes.size}`}
                          </p>
                          <div className="d-flex align-items-center mb-2">
                            {item.salePrice && item.salePrice < item.price ? (
                              <>
                                <span className="fw-bold me-2">{formatCurrency(item.salePrice)}</span>
                                <span className="text-muted text-decoration-line-through me-2">
                                  {formatCurrency(item.price)}
                                </span>
                                <Badge bg="success">
                                  {Math.round(((item.price - item.salePrice) / item.price) * 100)}% OFF
                                </Badge>
                              </>
                            ) : (
                              <span className="fw-bold">{formatCurrency(item.price)}</span>
                            )}
                          </div>
                        </div>
                        <Button
                          variant="outline-danger"
                          size="sm"
                          onClick={() => handleRemoveItem(item._id)}
                        >
                          ×
                        </Button>
                      </div>

                      <div className="d-flex justify-content-between align-items-center">
                        <div className="d-flex align-items-center">
                          <Button
                            variant="outline-secondary"
                            size="sm"
                            onClick={() => handleQuantityChange(item._id, item.quantity - 1)}
                            disabled={item.quantity <= 1}
                          >
                            -
                          </Button>
                          <span className="mx-3">{item.quantity}</span>
                          <Button
                            variant="outline-secondary"
                            size="sm"
                            onClick={() => handleQuantityChange(item._id, item.quantity + 1)}
                          >
                            +
                          </Button>
                        </div>
                        <div className="text-end">
                          <strong>{formatCurrency((item.salePrice || item.price) * item.quantity)}</strong>
                        </div>
                      </div>
                    </Card.Body>
                  </Col>
                </Row>
              </Card>
            ))}
          </Col>

          <Col lg={4}>
            <Card className="order-summary sticky-top" style={{ top: '20px' }}>
              <Card.Header className="bg-dark text-white">
                <h5 className="mb-0">Order Summary</h5>
              </Card.Header>
              <Card.Body>
                {/* Shukran Loyalty Program */}
                <div className="mb-4">
                  <div className="bg-light p-3 rounded mb-3">
                    <small className="text-muted">
                      Your order could earn you 151 Shukrans, Exclusive Offers And More
                    </small>
                  </div>
                  <div className="text-center p-3 bg-light rounded">
                    <div className="mb-2">
                      <strong>S Shukran</strong>
                    </div>
                    <Button variant="outline-primary" size="sm">
                      Join Now and Enjoy Benefits &gt;
                    </Button>
                  </div>
                </div>

                {/* Available Coupons */}
                <div className="mb-4">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h6 className="mb-0">Available Coupons</h6>
                    <Button 
                      variant="link" 
                      size="sm"
                      onClick={handleGetAvailableCoupons}
                    >
                      {showAvailableCoupons ? 'Hide' : 'Show'}
                    </Button>
                  </div>

                  {showAvailableCoupons && availableCoupons.length > 0 && (
                    <div className="mb-3">
                      {availableCoupons.map((coupon) => (
                        <div key={coupon.id} className="border rounded p-2 mb-2">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <strong>{coupon.code}</strong>
                              <small className="d-block text-muted">{coupon.description}</small>
                            </div>
                            <Button 
                              variant="outline-primary" 
                              size="sm"
                              onClick={() => {
                                setCouponCode(coupon.code);
                                handleApplyCoupon();
                              }}
                            >
                              Apply
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                {/* Coupon Section */}
                <div className="mb-4">
                  <h6 className="mb-2">Apply coupon/Voucher code</h6>
                  <div className="d-flex">
                    <Form.Control
                      type="text"
                      placeholder="Enter Coupon Code"
                      size="sm"
                      className="me-2"
                      value={couponCode}
                      onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                      onKeyPress={(e) => e.key === 'Enter' && handleCouponValidation()}
                    />
                    <Button 
                      variant="outline-primary" 
                      size="sm"
                      onClick={handleCouponValidation}
                      disabled={!couponCode.trim()}
                    >
                      Apply
                    </Button>
                  </div>
                </div>

                {/* Applied Coupons */}
                {cart.appliedCoupons && cart.appliedCoupons.length > 0 && (
                  <div className="mb-4">
                    <h6 className="mb-2">Applied Coupons</h6>
                    {cart.appliedCoupons.map((coupon, index) => (
                      <div key={index} className="border rounded p-2 mb-2 bg-light">
                        <div className="d-flex justify-content-between align-items-center">
                          <div>
                            <strong>{coupon.code}</strong>
                            <small className="d-block text-success">
                              -{formatCurrency(coupon.discountAmount)}
                            </small>
                          </div>
                          <Button 
                            variant="outline-danger" 
                            size="sm"
                            onClick={() => handleRemoveCoupon(coupon.code)}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {/* Price Breakdown */}
                <div className="price-breakdown">
                  <div className="d-flex justify-content-between mb-2">
                    <span>Subtotal:</span>
                    <span>{formatCurrency(cart.subtotal)}</span>
                  </div>

                  {cart.totalDiscount > 0 && (
                    <div className="d-flex justify-content-between mb-2 text-success">
                      <span>Discount:</span>
                      <span>-{formatCurrency(cart.totalDiscount)}</span>
                    </div>
                  )}

                  <hr />
                  <div className="d-flex justify-content-between mb-3">
                    <strong>Total:</strong>
                    <strong>{formatCurrency(cart.total)}</strong>
                  </div>
                </div>

                {/* Payment Options */}
                <Button variant="primary" className="w-100 mb-2" size="lg">
                  Proceed to Checkout
                </Button>

                <Button variant="outline-secondary" className="w-100">
                  Continue Shopping
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default CartComponent;
