import React, { useState, useEffect, useCallback } from 'react';
import { Row, Col, Card, Badge, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useCartActions } from '../../context/CartContext';
import ProductSkeleton from './ProductSkeleton';

const ProductGridComponent = ({ config, data }) => {
  const {
    title = 'Featured Products',
    products = [],
    gridColumns = 4,
    showPagination = false
  } = data;

  const { addToCart } = useCartActions();

  const [displayedProducts, setDisplayedProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const productsPerPage = 10;

  // Initialize displayed products
  useEffect(() => {
    if (products.length > 0) {
      setDisplayedProducts(products.slice(0, productsPerPage));
      setHasMore(products.length > productsPerPage);
    }
  }, [products]);

  // Load more products function
  const loadMoreProducts = useCallback(() => {
    if (loading || !hasMore) return;

    setLoading(true);

    // Simulate API delay
    setTimeout(() => {
      const nextPage = currentPage + 1;
      const startIndex = (nextPage - 1) * productsPerPage;
      const endIndex = startIndex + productsPerPage;
      const newProducts = products.slice(startIndex, endIndex);

      setDisplayedProducts(prev => [...prev, ...newProducts]);
      setCurrentPage(nextPage);
      setHasMore(endIndex < products.length);
      setLoading(false);
    }, 1000);
  }, [currentPage, products, loading, hasMore]);

  // Infinite scroll handler
  useEffect(() => {
    const handleScroll = () => {
      if (window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - 1000) {
        loadMoreProducts();
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loadMoreProducts]);

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const renderPrice = (product) => {
    if (product.salePrice && product.salePrice < product.price) {
      const discount = Math.round(((product.price - product.salePrice) / product.price) * 100);
      return (
        <div className="d-flex align-items-center">
          <span className="fw-bold me-2">{formatCurrency(product.salePrice)}</span>
          <span className="text-muted text-decoration-line-through me-2">
            {formatCurrency(product.price)}
          </span>
          <span className="text-success small">{discount}% OFF</span>
        </div>
      );
    }
    return <span className="fw-bold">{formatCurrency(product.price)}</span>;
  };

  const handleAddToCart = async (e, product) => {
    e.preventDefault();
    e.stopPropagation();

    try {
      const result = await addToCart(product.id, 1);
      if (result.success) {
        console.log('Product added to cart successfully!');
      } else {
        console.error('Failed to add product to cart:', result.error);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const getColClass = () => {
    switch (gridColumns) {
      case 2: return 'col-md-6';
      case 3: return 'col-md-4';
      case 4: return 'col-md-3';
      case 6: return 'col-md-2';
      default: return 'col-md-3';
    }
  };

  return (
    <div className="product-grid-section py-5">
      <div className="container">
        {/* Simple Banner */}
        <div className="text-center mb-5">
          <h1 className="display-4 fw-bold mb-3">Discover Amazing Products</h1>
          <p className="lead text-muted">Shop the latest trends and find your perfect style</p>
        </div>

        <h2 className="text-center mb-5 fw-bold">{title}</h2>

        <Row>
          {displayedProducts.map((product) => (
            <Col key={product.id} className={`${getColClass()} mb-4`}>
              <Link to={`/product/${product.id}`} className="text-decoration-none">
                <Card className="product-card h-100 border-0 shadow-sm">
                  <div className="position-relative">
                    <Card.Img
                      variant="top"
                      src={product.image || 'https://via.placeholder.com/300x400'}
                      style={{ height: '400px', objectFit: 'cover' }}
                      className="product-image"
                    />

                    {/* First on Styli Badge */}
                    <div className="position-absolute top-0 start-0 m-2">
                      <Badge bg="dark" className="px-2 py-1">
                        First on Styli
                      </Badge>
                    </div>

                    {/* Wishlist Button */}
                    <div className="position-absolute bottom-0 end-0 m-2">
                      <button
                        className="btn btn-light rounded-circle shadow-sm"
                        style={{ width: '40px', height: '40px' }}
                        onClick={(e) => {
                          e.preventDefault();
                          e.stopPropagation();
                        }}
                      >
                        <i className="fas fa-heart text-muted"></i>
                      </button>
                    </div>
                  </div>

                  <Card.Body className="d-flex flex-column p-3">
                    <div className="mb-2">
                      {renderPrice(product)}
                    </div>

                    <Card.Title className="mb-2 text-dark" style={{ fontSize: '0.9rem', fontWeight: '500' }}>
                      {product.name}
                    </Card.Title>

                    {/* Summer Holiday Edit Badge */}
                    {product.category === 'Clothing' && (
                      <div className="mb-2">
                        <Badge bg="primary" className="small">Summer Holiday Edit</Badge>
                      </div>
                    )}
                  </Card.Body>
                </Card>
              </Link>
            </Col>
          ))}
        </Row>

        {/* Loading skeleton */}
        {loading && <ProductSkeleton count={8} />}

        {/* Load more button (fallback) */}
        {hasMore && !loading && (
          <div className="text-center mt-4">
            <Button 
              variant="outline-primary" 
              onClick={loadMoreProducts}
              className="px-4 py-2"
            >
              Load More Products
            </Button>
          </div>
        )}

        {/* End of products message */}
        {!hasMore && displayedProducts.length > 0 && (
          <div className="text-center mt-4">
            <p className="text-muted">You've reached the end of products</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductGridComponent;
