import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const CategoryListComponent = ({ config, data }) => {
  const { title, categories = [] } = data;

  const renderCategoryCard = (category) => (
    <Col key={category.name} className="col-md-4 col-lg-3 mb-4">
      <Link to={category.link} className="text-decoration-none">
        <Card className="category-card h-100">
          <div className="position-relative">
            <Card.Img
              variant="top"
              src={category.image}
              style={{ height: '200px', objectFit: 'cover' }}
            />
            <div className="position-absolute bottom-0 start-0 w-100 p-3" 
                 style={{ background: 'linear-gradient(transparent, rgba(0,0,0,0.7))' }}>
              <h6 className="text-white mb-0 fw-bold">{category.name}</h6>
            </div>
          </div>
        </Card>
      </Link>
    </Col>
  );

  return (
    <div className="category-list-section py-5">
      <div className="container">
        <h2 className="text-center mb-5 fw-bold">{title}</h2>

        <Row>
          {categories.map(renderCategoryCard)}
        </Row>

        {categories.length === 0 && (
          <div className="text-center py-4">
            <p className="text-muted">No categories available</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CategoryListComponent;
