import React from 'react';
import { Button, InputGroup, FormControl, Spinner, Badge, Dropdown } from 'react-bootstrap';

const CartItem = React.memo(({ item, onUpdateQuantity, onRemove, formatCurrency, loading }) => {
  const price = item.salePrice || item.price;
  const itemTotal = price * item.quantity;
  const couponDiscount = item.quantity === 2 ? 3.90 : 36.63; // Mock coupon discount based on quantity

  const handleQuantityChange = (operation) => {
    let newQuantity = item.quantity;
    if (operation === 'increase') {
      newQuantity += 1;
    } else if (operation === 'decrease' && item.quantity > 1) {
      newQuantity -= 1;
    }
    onUpdateQuantity(item._id, newQuantity);
  };

  const handleMoveToWishlist = () => {
    // Wishlist functionality here
    console.log('Move to wishlist:', item._id);
  };

  return (
    <div className="cart-item mb-4 p-3 border rounded">
      <div className="row">
        {/* Product Image */}
        <div className="col-md-3">
          <img
            src={item.image || 'https://via.placeholder.com/150x200'}
            alt={item.name}
            className="w-100 rounded"
            style={{ height: '200px', objectFit: 'cover' }}
          />
        </div>

        {/* Product Details */}
        <div className="col-md-9">
          <div className="d-flex justify-content-between align-items-start">
            <div className="flex-grow-1">
              {/* Product Name */}
              <h6 className="mb-2 fw-bold">{item.name}</h6>

              {/* Size Dropdown */}
              <div className="mb-3">
                <Dropdown>
                  <Dropdown.Toggle variant="outline-secondary" size="sm">
                    Size: L <i className="fas fa-chevron-down ms-1"></i>
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                    <Dropdown.Item>Size: XS</Dropdown.Item>
                    <Dropdown.Item>Size: S</Dropdown.Item>
                    <Dropdown.Item>Size: M</Dropdown.Item>
                    <Dropdown.Item>Size: L</Dropdown.Item>
                    <Dropdown.Item>Size: XL</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>

              {/* Quantity Controls */}
              <div className="mb-3">
                <div className="d-flex align-items-center">
                  <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={() => handleQuantityChange('decrease')}
                    disabled={item.quantity <= 1 || loading}
                    style={{ width: '35px', height: '35px' }}
                  >
                    {loading ? <Spinner size="sm" animation="border" /> : '-'}
                  </Button>
                  <span className="mx-3 fw-bold">{item.quantity}</span>
                  <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={() => handleQuantityChange('increase')}
                    disabled={loading}
                    style={{ width: '35px', height: '35px' }}
                  >
                    {loading ? <Spinner size="sm" animation="border" /> : '+'}
                  </Button>
                </div>
              </div>

              {/* Badge and Coupon Discount */}
              <div className="mb-3">
                <Badge bg="dark" className="me-3">20+ Sold</Badge>
                <div className="text-success small">
                  Coupon discount: {formatCurrency(couponDiscount)}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="d-flex gap-2">
                <Button
                  variant="link"
                  className="text-danger p-0"
                  onClick={() => onRemove(item._id)}
                  disabled={loading}
                >
                  {loading ? <Spinner size="sm" animation="border" /> : 'Remove'}
                </Button>
                <Button
                  variant="link"
                  className="text-muted p-0"
                  onClick={handleMoveToWishlist}
                >
                  Move to Wishlist
                </Button>
              </div>
            </div>

            {/* Price */}
            <div className="text-end">
              <div className="fw-bold fs-5">{formatCurrency(itemTotal)}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
});

CartItem.displayName = 'CartItem';

export default CartItem;
