import { useState, useEffect, useCallback } from 'react';

const useServerDrivenUI = (pageType, pageId, userContext = {}) => {
  const [layout, setLayout] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastFetched, setLastFetched] = useState(null);

  const fetchLayout = useCallback(async () => {
    try {
      console.log('🔄 Fetching layout:', { pageType, pageId, userContext });
      setLoading(true);
      setError(null);

      const queryParams = new URLSearchParams({
        ...userContext,
        timestamp: Date.now() // Cache busting
      });

      const apiUrl = `http://localhost:3000/api/layouts/${pageType}/${pageId}?${queryParams}`;
      console.log('📡 API URL:', apiUrl);

      // Use the full backend URL since React app is on port 3001 and backend is on port 3000
      const response = await fetch(apiUrl);
      console.log('📥 Response status:', response.status);

      const data = await response.json();
      console.log('📦 Response data:', data);

      if (data.success) {
        setLayout(data.layout);
        setLastFetched(Date.now());
        console.log('✅ Layout loaded successfully');
      } else {
        setError(data.message || 'Failed to load layout');
        console.error('❌ Layout load failed:', data.message);
      }
    } catch (error) {
      console.error('💥 Error fetching layout:', error);
      setError('Failed to load page layout');
    } finally {
      setLoading(false);
    }
  }, [pageType, pageId, userContext]);

  const refreshLayout = useCallback(() => {
    console.log('🔄 Refreshing layout...');
    fetchLayout();
  }, [fetchLayout]);

  const updateComponentData = useCallback((componentId, newData) => {
    if (!layout) return;

    setLayout(prevLayout => ({
      ...prevLayout,
      components: prevLayout.components.map(component =>
        component.id === componentId
          ? { ...component, data: { ...component.data, ...newData } }
          : component
      )
    }));
  }, [layout]);

  const getComponentById = useCallback((componentId) => {
    if (!layout) return null;
    return layout.components.find(component => component.id === componentId);
  }, [layout]);

  const getComponentsByType = useCallback((componentType) => {
    if (!layout) return [];
    return layout.components.filter(component => component.type === componentType);
  }, [layout]);

  useEffect(() => {
    console.log('🎯 useServerDrivenUI effect triggered:', { pageType, pageId });
    fetchLayout();
  }, [fetchLayout]);

  return {
    layout,
    loading,
    error,
    lastFetched,
    refreshLayout,
    updateComponentData,
    getComponentById,
    getComponentsByType
  };
};

export default useServerDrivenUI;
