import React from 'react';

const CheckoutComponent = ({ config, data }) => {
  const { items = [], subtotal = 0, shipping = 0, total = 0, appliedCoupons = [] } = data;

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  // Handle different image field formats
  const getItemImage = (item) => {
    if (item.image) return item.image;
    if (item.images && item.images.length > 0) return item.images[0];
    return 'https://via.placeholder.com/100x100';
  };

  return (
    <div className="container py-5">
      <h2 className="mb-4">Checkout</h2>

      <div className="row">
        <div className="col-lg-8">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Order Summary</h5>
            </div>
            <div className="card-body">
              {items.map((item, index) => (
                <div key={index} className="d-flex border-bottom py-3">
                  <img
                    src={getItemImage(item)}
                    alt={item.name}
                    className="rounded me-3"
                    style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                  />
                  <div className="flex-grow-1">
                    <h6 className="mb-1">{item.name}</h6>
                    <p className="text-muted small mb-2">
                      Quantity: {item.quantity}
                    </p>
                    <div className="fw-bold">
                      {formatCurrency((item.salePrice || item.price) * item.quantity)}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="col-lg-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Payment Summary</h5>
            </div>
            <div className="card-body">
              <div className="d-flex justify-content-between mb-2">
                <span>Subtotal:</span>
                <span>{formatCurrency(subtotal)}</span>
              </div>

              <div className="d-flex justify-content-between mb-2">
                <span>Shipping:</span>
                <span>{formatCurrency(shipping)}</span>
              </div>

              {appliedCoupons && appliedCoupons.length > 0 && (
                <div className="d-flex justify-content-between mb-2 text-success">
                  <span>Discount:</span>
                  <span>-{formatCurrency(appliedCoupons.reduce((sum, coupon) => sum + coupon.discountAmount, 0))}</span>
                </div>
              )}

              <hr />
              <div className="d-flex justify-content-between mb-3">
                <strong>Total:</strong>
                <strong>{formatCurrency(total)}</strong>
              </div>

              <button className="btn btn-primary w-100">
                Complete Purchase
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckoutComponent;
