import React, { memo } from 'react';
import { Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ProductCard = memo(({ product }) => {
  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(0)}`;
  };

  const renderPrice = () => {
    if (product.salePrice && product.salePrice < product.price) {
      const discount = Math.round(((product.price - product.salePrice) / product.price) * 100);
      return (
        <div className="d-flex align-items-center">
          <span className="fw-bold me-2">{formatCurrency(product.salePrice)}</span>
          <span className="text-muted text-decoration-line-through me-2">
            {formatCurrency(product.price)}
          </span>
          <Badge bg="success" className="small px-2 py-1">{discount}% OFF</Badge>
        </div>
      );
    }
    return <span className="fw-bold">{formatCurrency(product.price)}</span>;
  };

  return (
    <Link to={`/product/${product._id}`} className="text-decoration-none">
      <div className="product-card bg-white rounded shadow-sm position-relative overflow-hidden">
        <div className="position-relative">
          <img
            src={product.images && product.images.length > 0 ? product.images[0] : 'https://via.placeholder.com/300x400'}
            alt={product.name}
            className="w-100"
            style={{ height: '400px', objectFit: 'cover' }}
          />

          {/* Discount Badge - Top Left */}
          {product.salePrice && product.salePrice < product.price && (
            <div className="position-absolute top-0 start-0 m-2">
              <Badge bg="danger" className="px-2 py-1">
                {Math.round(((product.price - product.salePrice) / product.price) * 100)}% OFF
              </Badge>
            </div>
          )}

          {/* First on Styli Badge - Top Right */}
          <div className="position-absolute top-0 end-0 m-2">
            <Badge bg="dark" className="px-2 py-1 small">First on Styli</Badge>
          </div>

          {/* Heart Button - Top Right, smaller and more subtle */}
          <div className="position-absolute top-0 end-0 m-2" style={{ marginTop: '2.5rem' }}>
            <button
              className="btn btn-light rounded-circle shadow-sm"
              style={{ 
                width: '28px', 
                height: '28px',
                opacity: '0.9',
                transition: 'all 0.2s ease'
              }}
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
              }}
              onMouseEnter={(e) => {
                e.target.style.opacity = '1';
                e.target.style.transform = 'scale(1.1)';
              }}
              onMouseLeave={(e) => {
                e.target.style.opacity = '0.9';
                e.target.style.transform = 'scale(1)';
              }}
            >
              <i className="fas fa-heart text-muted" style={{ fontSize: '0.8rem' }}></i>
            </button>
          </div>
        </div>

        <div className="p-3">
          <div className="mb-2">
            {renderPrice()}
          </div>

          <div className="mb-2">
            <h6 className="mb-0 text-dark" style={{ fontSize: '0.9rem', lineHeight: '1.3' }}>
              {product.name}
            </h6>
          </div>

          {product.category === 'Summer Holiday Edit' && (
            <div className="mb-2">
              <Badge bg="primary" className="small px-2 py-1">Summer Holiday Edit</Badge>
            </div>
          )}
        </div>
      </div>
    </Link>
  );
});

export default ProductCard;
