import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';

const HeaderComponent = ({ config, data }) => {
  const { cart, loadCart } = useCart();
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const itemCount = cart.items.reduce((total, item) => total + item.quantity, 0);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  return (
    <>
      <nav className="navbar-top">
        <div className="container">
          <div className="d-flex align-items-center justify-content-between">
            <Link to="/" className="navbar-brand">
              <span className="fw-bold">styli</span>
              <span className="arabic">ستايلي</span>
            </Link>

            <div className="navbar-nav-main d-none d-lg-flex">
              <Link to="/women" className="nav-link">WOMEN</Link>
              <Link to="/men" className="nav-link">MEN</Link>
              <Link to="/kids" className="nav-link">KIDS</Link>
              <Link to="/beauty" className="nav-link">BEAUTY</Link>
              <Link to="/" className="nav-link">HOME</Link>
            </div>

            <div className="user-actions">
              <Link to="/profile" className="btn">
                <i className="fas fa-user"></i>
              </Link>
              <Link to="/wishlist" className="btn">
                <i className="fas fa-heart"></i>
              </Link>
              <Link to="/cart" className="btn cart-badge">
                <i className="fas fa-shopping-bag"></i>
                {itemCount > 0 && (
                  <span className="badge">{itemCount}</span>
                )}
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="d-md-none bg-light py-2">
        <div className="container">
          <form onSubmit={handleSearch}>
            <div className="input-group">
              <span className="input-group-text">
                <i className="fas fa-search"></i>
              </span>
              <input
                type="text"
                className="form-control"
                placeholder="What are you looking for?"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default HeaderComponent;
