import React from 'react';
import { Button, InputGroup, FormControl, Spinner, Badge, Dropdown } from 'react-bootstrap';

const CartItem = React.memo(({ item, onUpdateQuantity, onRemove, formatCurrency, loading }) => {
  const price = item.salePrice || item.price;
  const itemTotal = price * item.quantity;
  const couponDiscount = item.quantity === 2 ? 3.90 : 36.63; // Mock coupon discount based on quantity

  const handleQuantityChange = (operation) => {
    let newQuantity = item.quantity;
    if (operation === 'increase') {
      newQuantity += 1;
    } else if (operation === 'decrease' && item.quantity > 1) {
      newQuantity -= 1;
    }
    onUpdateQuantity(item._id, newQuantity);
  };

  const handleMoveToWishlist = () => {
    // Wishlist functionality here
    console.log('Move to wishlist:', item._id);
  };

  return (
    <div className="cart-item mb-4 p-4">
      <div className="row align-items-center">
        {/* Product Image */}
        <div className="col-md-3 col-lg-2">
          <div className="position-relative">
            <img
              src={item.image || 'https://via.placeholder.com/150x200'}
              alt={item.name}
              className="w-100 rounded-3"
              style={{ height: '180px', objectFit: 'cover' }}
            />
            {/* Wishlist Icon */}
            <button
              className="btn btn-light rounded-circle position-absolute top-0 end-0 m-2"
              style={{ width: '32px', height: '32px', fontSize: '0.8rem' }}
              onClick={handleMoveToWishlist}
            >
              <i className="fas fa-heart text-muted"></i>
            </button>
          </div>
        </div>

        {/* Product Details */}
        <div className="col-md-9 col-lg-10">
          <div className="row align-items-center">
            <div className="col-lg-8">
              {/* Product Name */}
              <h6 className="mb-3 fw-bold text-dark" style={{ fontSize: '1rem', lineHeight: '1.4' }}>
                {item.name}
              </h6>

              {/* Size and Quantity Row */}
              <div className="row align-items-center mb-3">
                <div className="col-md-6 mb-2 mb-md-0">
                  <Dropdown>
                    <Dropdown.Toggle variant="outline-secondary" size="sm">
                      Size: L <i className="fas fa-chevron-down ms-1"></i>
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item>Size: XS</Dropdown.Item>
                      <Dropdown.Item>Size: S</Dropdown.Item>
                      <Dropdown.Item>Size: M</Dropdown.Item>
                      <Dropdown.Item>Size: L</Dropdown.Item>
                      <Dropdown.Item>Size: XL</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </div>

                <div className="col-md-6">
                  <div className="d-flex align-items-center">
                    <span className="text-muted me-3 small">Qty:</span>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      onClick={() => handleQuantityChange('decrease')}
                      disabled={item.quantity <= 1 || loading}
                      style={{ width: '32px', height: '32px', padding: 0 }}
                    >
                      {loading ? <Spinner size="sm" animation="border" /> : '-'}
                    </Button>
                    <span className="mx-3 fw-bold">{item.quantity}</span>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      onClick={() => handleQuantityChange('increase')}
                      disabled={loading}
                      style={{ width: '32px', height: '32px', padding: 0 }}
                    >
                      {loading ? <Spinner size="sm" animation="border" /> : '+'}
                    </Button>
                  </div>
                </div>
              </div>

              {/* Badge and Coupon Discount */}
              <div className="mb-3">
                <Badge bg="dark" className="me-3 mb-2">20+ Sold</Badge>
                <div className="text-success small fw-semibold">
                  Coupon discount: {formatCurrency(couponDiscount)}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="d-flex gap-3">
                <Button
                  variant="link"
                  className="text-danger p-0 text-decoration-none"
                  onClick={() => onRemove(item._id)}
                  disabled={loading}
                >
                  <i className="fas fa-trash me-1"></i>
                  {loading ? <Spinner size="sm" animation="border" /> : 'Remove'}
                </Button>
                <Button
                  variant="link"
                  className="text-muted p-0 text-decoration-none"
                  onClick={handleMoveToWishlist}
                >
                  <i className="fas fa-heart me-1"></i>
                  Move to Wishlist
                </Button>
              </div>
            </div>

            {/* Price */}
            <div className="col-lg-4 text-lg-end mt-3 mt-lg-0">
              <div className="fw-bold fs-4 text-dark">{formatCurrency(itemTotal)}</div>
              {item.salePrice && item.salePrice < item.price && (
                <div className="text-muted small text-decoration-line-through">
                  {formatCurrency(item.price * item.quantity)}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
});

CartItem.displayName = 'CartItem';

export default CartItem;
