import React from 'react';
import { Link } from 'react-router-dom';

const HeroComponent = ({ config, data }) => {
  const {
    title = 'Welcome to Styli',
    subtitle = 'Discover amazing fashion',
    backgroundImage = 'https://images.unsplash.com/photo-1441986300917-64674bd600d8',
    ctaText = 'Shop Now',
    ctaLink = '/products',
    showSearch = true
  } = config;

  return (
    <div 
      className="hero-section position-relative"
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '500px'
      }}
    >
      <div className="container">
        <div className="row align-items-center" style={{ minHeight: '500px' }}>
          <div className="col-lg-6">
            <div className="text-white">
              <h1 className="display-4 fw-bold mb-3">{title}</h1>
              <p className="lead mb-4">{subtitle}</p>

              {showSearch && (
                <div className="mb-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="form-control form-control-lg"
                      placeholder="Search for products..."
                    />
                    <button className="btn btn-primary btn-lg" type="button">
                      <i className="fas fa-search"></i>
                    </button>
                  </div>
                </div>
              )}

              <Link to={ctaLink} className="btn btn-light btn-lg">
                {ctaText}
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroComponent;
