import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Spinner, Card, Button, Form, InputGroup, Alert } from 'react-bootstrap';
import CartItem from './CartItem';
import EmptyCart from './EmptyCart';

const Cart = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true); // Only for initial load
  const [error, setError] = useState(null);
  const [couponCode, setCouponCode] = useState('');
  const [couponError, setCouponError] = useState('');
  const [couponSuccess, setCouponSuccess] = useState('');
  const [itemLoading, setItemLoading] = useState({}); // { [itemId]: true/false }

  // Fetch cart data when component mounts
  useEffect(() => {
    fetchCart();
    // eslint-disable-next-line
  }, []);

  // Fetch cart data from API
  const fetchCart = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/cart');
      if (response.data && response.data.success) {
        setCart(response.data.cart);
      } else {
        setError('Failed to retrieve cart data');
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
      setError('An error occurred while fetching cart data');
    } finally {
      setLoading(false);
    }
  };

  // Update cart item quantity
  const handleQuantityUpdate = useCallback(async (itemId, quantity) => {
    setItemLoading(prev => ({ ...prev, [itemId]: true }));
    try {
      const response = await axios.put(`/api/cart/items/${itemId}`, { quantity });
      if (response.data && response.data.success) {
        setCart(response.data.cart);
      } else {
        setError(response.data.message || 'Failed to update quantity');
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      setError('An error occurred while updating the quantity');
    } finally {
      setItemLoading(prev => ({ ...prev, [itemId]: false }));
    }
  }, []);

  // Remove cart item
  const handleRemoveItem = useCallback(async (itemId) => {
    setItemLoading(prev => ({ ...prev, [itemId]: true }));
    try {
      const response = await axios.delete(`/api/cart/items/${itemId}`);
      if (response.data && response.data.success) {
        setCart(response.data.cart);
      } else {
        setError(response.data.message || 'Failed to remove item');
      }
    } catch (error) {
      console.error('Error removing item:', error);
      setError('An error occurred while removing the item');
    } finally {
      setItemLoading(prev => ({ ...prev, [itemId]: false }));
    }
  }, []);

  // Handle coupon application
  const handleApplyCoupon = useCallback(async (e) => {
    e.preventDefault();
    if (!couponCode.trim()) {
      setCouponError('Please enter a coupon code');
      return;
    }
    setCouponError('');
    setCouponSuccess('');
    setLoading(true);
    try {
      const response = await axios.post('/api/cart/coupons', { couponCode });
      if (response.data && response.data.success) {
        setCart(response.data.cart);
        setCouponSuccess('Coupon applied successfully!');
        setCouponCode('');
      } else {
        setCouponError(response.data.message || 'Failed to apply coupon');
      }
    } catch (error) {
      console.error('Error applying coupon:', error);
      setCouponError('An error occurred while applying the coupon');
    } finally {
      setLoading(false);
    }
  }, [couponCode]);

  // Handle coupon removal
  const handleRemoveCoupon = useCallback(async (code) => {
    setLoading(true);
    try {
      const response = await axios.delete(`/api/cart/coupons/${code}`);
      if (response.data && response.data.success) {
        setCart(response.data.cart);
      } else {
        setError(response.data.message || 'Failed to remove coupon');
      }
    } catch (error) {
      console.error('Error removing coupon:', error);
      setError('An error occurred while removing the coupon');
    } finally {
      setLoading(false);
    }
  }, []);

  // Format price as currency
  const formatCurrency = useCallback((amount) => {
    return `$${parseFloat(amount).toFixed(2)}`;
  }, []);

  // Check if cart is empty
  const isCartEmpty = !cart || !cart.items || cart.items.length === 0;

  return (
    <main className="container py-5">
      <h1 className="mb-4">Your Shopping Cart</h1>
      {/* Only show global loading overlay for initial load or coupon actions */}
      {loading && (
        <div className="loading-overlay">
          <Spinner animation="border" variant="primary" />
        </div>
      )}
      {/* Error message */}
      {error && (
        <Alert variant="danger" onClose={() => setError(null)} dismissible>
          {error}
        </Alert>
      )}
      {isCartEmpty ? (
        <EmptyCart />
      ) : (
        <div className="row">
          {/* Cart items */}
          <div className="col-md-8">
            {cart.items.map(item => (
              <CartItem
                key={item._id}
                item={item}
                onUpdateQuantity={handleQuantityUpdate}
                onRemove={handleRemoveItem}
                formatCurrency={formatCurrency}
                loading={!!itemLoading[item._id]}
              />
            ))}
          </div>
          {/* Order summary */}
          <div className="col-md-4">
            <Card className="summary-card">
              <Card.Body>
                <Card.Title>Order Summary</Card.Title>
                <div className="d-flex justify-content-between mb-2">
                  <span>Subtotal:</span>
                  <span>{formatCurrency(cart.subtotal)}</span>
                </div>
                {cart.totalDiscount > 0 && (
                  <div className="d-flex justify-content-between mb-2">
                    <span>Discount:</span>
                    <span>-{formatCurrency(cart.totalDiscount)}</span>
                  </div>
                )}
                <div className="d-flex justify-content-between mb-3">
                  <strong>Total:</strong>
                  <strong>{formatCurrency(cart.total)}</strong>
                </div>
                {/* Coupon form */}
                <Form onSubmit={handleApplyCoupon} className="mb-3">
                  <InputGroup>
                    <Form.Control
                      type="text"
                      placeholder="Promo code"
                      value={couponCode}
                      onChange={(e) => setCouponCode(e.target.value)}
                    />
                    <Button variant="outline-primary" type="submit" disabled={loading}>Apply</Button>
                  </InputGroup>
                  {couponError && <Form.Text className="text-danger">{couponError}</Form.Text>}
                  {couponSuccess && <Form.Text className="text-success">{couponSuccess}</Form.Text>}
                </Form>
                {/* Applied coupons */}
                {cart.appliedCoupons && cart.appliedCoupons.length > 0 && (
                  <div className="mb-3">
                    <p className="mb-2">Applied coupons:</p>
                    <div>
                      {cart.appliedCoupons.map(coupon => (
                        <div key={coupon.code} className="d-flex justify-content-between align-items-center mb-1">
                          <span className="badge bg-success">{coupon.code}</span>
                          <Button
                            variant="link"
                            className="text-danger p-0"
                            onClick={() => handleRemoveCoupon(coupon.code)}
                            disabled={loading}
                          >
                            Remove
                          </Button>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
                {/* Checkout button */}
                <div className="d-grid">
                  <Button variant="primary">Proceed to Checkout</Button>
                </div>
              </Card.Body>
            </Card>
          </div>
        </div>
      )}
    </main>
  );
};

export default Cart;
