import React from 'react';
import { Button, InputGroup, FormControl, Spinner } from 'react-bootstrap';

const CartItem = ({ item, onUpdateQuantity, onRemove, formatCurrency, loading }) => {
  const price = item.salePrice || item.price;
  const itemTotal = price * item.quantity;

  const handleQuantityChange = (operation) => {
    let newQuantity = item.quantity;
    if (operation === 'increase') {
      newQuantity += 1;
    } else if (operation === 'decrease' && item.quantity > 1) {
      newQuantity -= 1;
    }
    onUpdateQuantity(item._id, newQuantity);
  };

  const renderAttributes = () => {
    if (!item.attributes || Object.keys(item.attributes).length === 0) {
      return null;
    }
    return (
      <div className="text-muted mb-2 small">
        {Object.entries(item.attributes).map(([key, value]) => (
          <span key={key} className="me-2">
            {key}: {value}
          </span>
        ))}
      </div>
    );
  };

  return (
    <div className="cart-item d-flex">
      <div className="me-3">
        <img
          src={item.image || 'https://via.placeholder.com/100'}
          alt={item.name}
          className="item-image"
        />
      </div>
      <div className="item-details">
        <h5>{item.name}</h5>
        <p className="text-muted mb-2">{formatCurrency(price)}</p>
        {renderAttributes()}
        <div className="d-flex justify-content-between align-items-center mt-3">
          <InputGroup className="quantity-control">
            <Button
              variant="outline-secondary"
              onClick={() => handleQuantityChange('decrease')}
              disabled={item.quantity <= 1 || loading}
            >
              {loading ? <Spinner size="sm" animation="border" /> : '-'}
            </Button>
            <FormControl
              className="text-center"
              value={item.quantity}
              readOnly
            />
            <Button
              variant="outline-secondary"
              onClick={() => handleQuantityChange('increase')}
              disabled={loading}
            >
              {loading ? <Spinner size="sm" animation="border" /> : '+'}
            </Button>
          </InputGroup>
          <div className="text-end">
            <div className="fw-bold mb-2">{formatCurrency(itemTotal)}</div>
            <Button
              variant="link"
              className="text-danger p-0"
              onClick={() => onRemove(item._id)}
              disabled={loading}
            >
              {loading ? <Spinner size="sm" animation="border" /> : 'Remove'}
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
