import React from 'react';
import { Button, Form, InputGroup } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const HeroComponent = ({ config, data }) => {
  const {
    title = 'Welcome to Styli',
    subtitle = 'Discover the latest fashion trends',
    backgroundImage = 'https://images.unsplash.com/photo-1441986300917-64674bd600d8',
    ctaText = 'Shop Now',
    ctaLink = '/products',
    showSearch = true
  } = data;

  return (
    <div 
      className="hero-section position-relative"
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '500px'
      }}
    >
      <div className="container h-100">
        <div className="row h-100 align-items-center">
          <div className="col-lg-6">
            <div className="hero-content text-white">
              <h1 className="display-4 fw-bold mb-3">{title}</h1>
              <p className="lead mb-4">{subtitle}</p>

              {showSearch && (
                <div className="search-section mb-4">
                  <InputGroup size="lg">
                    <Form.Control
                      type="text"
                      placeholder="Search for products..."
                      className="border-0"
                    />
                    <Button variant="primary" className="border-0">
                      <i className="fas fa-search"></i>
                    </Button>
                  </InputGroup>
                </div>
              )}

              <Link to={ctaLink}>
                <Button variant="light" size="lg" className="fw-bold">
                  {ctaText}
                </Button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroComponent;
