import React, { memo } from 'react';
import { Link } from 'react-router-dom';
import { useCartSummary } from '../context/CartContext';

const Header = memo(() => {
  const { cartSummary } = useCartSummary();

  return (
    <header className="bg-dark py-3">
      <div className="container">
        <div className="d-flex justify-content-between align-items-center">
          <Link to="/" className="text-decoration-none">
            <h1 className="h4 text-white mb-0">Stylii</h1>
          </Link>
          <div className="cart-summary d-flex align-items-center">
            <Link to="/cart" className="d-flex align-items-center text-decoration-none">
              <span className="badge bg-primary me-2">{cartSummary.itemCount}</span>
              <span className="text-white me-2">Cart</span>
              <span className="text-white small">${cartSummary.total.toFixed(2)}</span>
            </Link>
          </div>
        </div>
      </div>
    </header>
  );
});

export default Header;
