import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Badge, Button, Form } from 'react-bootstrap';
import { useCartActions, useCartData } from '../../context/CartContext';
import { useNavigate } from 'react-router-dom';

const ProductDetailComponent = ({ config, data }) => {
  const { product = {} } = data;
  const { addToCart } = useCartActions();
  const { cart } = useCartData();
  const navigate = useNavigate();

  const [selectedSize, setSelectedSize] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [sizeError, setSizeError] = useState('');
  const [isInCart, setIsInCart] = useState(false);
  const [cartItem, setCartItem] = useState(null);

  // Check if product is already in cart
  useEffect(() => {
    if (cart && cart.items) {
      const existingItem = cart.items.find(item => item.productId === product.id);
      if (existingItem) {
        setIsInCart(true);
        setCartItem(existingItem);
        setSelectedSize(existingItem.attributes?.size || '');
        setQuantity(existingItem.quantity);
      } else {
        setIsInCart(false);
        setCartItem(null);
      }
    }
  }, [cart, product.id]);

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const renderPrice = () => {
    if (product.salePrice && product.salePrice < product.price) {
      const savings = product.price - product.salePrice;
      return (
        <div className="price-section p-3 mb-3">
          <div className="mb-2">
            <span className="h4 fw-bold">Buy it for {formatCurrency(product.salePrice)} {formatCurrency(product.price)}</span>
          </div>
          <div className="mb-2">
            <span className="text-success">Save {formatCurrency(savings)}</span>
          </div>
          <div className="mb-2">
            <span className="text-success">Get extra 50% off your first order</span>
          </div>
          <div className="mb-2">
            <Button variant="outline-danger" size="sm" className="coupon-button">
              NEW50
            </Button>
            <small className="text-muted d-block">Tap to copy</small>
          </div>
        </div>
      );
    }
    return (
      <div className="price-section p-3 mb-3">
        <div className="mb-2">
          <span className="h4 fw-bold">{formatCurrency(product.price)}</span>
        </div>
      </div>
    );
  };

  const handleAddToCart = async () => {
    if (!selectedSize) {
      setSizeError('Please select a size');
      return;
    }

    setSizeError('');

    try {
      const result = await addToCart(product.id, quantity, selectedSize);
      if (result.success) {
        console.log('Product added to cart successfully!');
        setIsInCart(true);
      } else {
        console.error('Failed to add product to cart:', result.error);
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleViewInBag = () => {
    navigate('/cart');
  };

  const sizes = [
    { size: 'S', stock: 12 },
    { size: 'M', stock: 15 },
    { size: 'L', stock: 10 },
    { size: 'XL', stock: 8 },
    { size: 'XXL', stock: 5 },
    { size: '3XL', stock: 3 },
    { size: '4XL', stock: 2 }
  ];

  return (
    <div className="product-detail-section py-5">
      <Container>
        {/* Breadcrumbs */}
        <div className="breadcrumbs mb-4">
          <small className="text-muted">
            Home &gt; Men &gt; {product.subcategory || 'Clothing'} &gt; {product.name}
          </small>
        </div>

        <Row>
          {/* Product Images */}
          <Col lg={6} className="mb-4">
            <div className="product-images">
              <div className="main-image mb-3">
                <img
                  src={product.image || 'https://via.placeholder.com/600x800'}
                  alt={product.name}
                  className="img-fluid rounded"
                  style={{ width: '100%', height: '600px', objectFit: 'cover' }}
                />
              </div>
              <div className="main-image">
                <img
                  src={product.image || 'https://via.placeholder.com/600x800'}
                  alt={product.name}
                  className="img-fluid rounded"
                  style={{ width: '100%', height: '600px', objectFit: 'cover' }}
                />
              </div>
            </div>
          </Col>

          {/* Product Details */}
          <Col lg={6}>
            <div className="product-details">
              {/* First on Styli Badge */}
              <div className="mb-3">
                <Badge bg="secondary" className="px-2 py-1">First on Styli</Badge>
              </div>

              {/* Brand and Product Name */}
              <h1 className="h3 mb-2 fw-bold">{product.brand || 'FAV'}</h1>
              <h2 className="h4 mb-3 text-muted">{product.name}</h2>

              {renderPrice()}

              {/* Shukrans Program */}
              <div className="mb-3">
                <span className="text-muted">S Earn 8 Shukrans + 1 Tier Bonuses</span>
              </div>

              {/* Payment Options */}
              <div className="mb-3">
                <small className="text-muted">
                  Pay {formatCurrency(product.price ? product.price * 0.25 : 9.75)} now and the rest in 3 payments over time. No hidden fees, no interest!
                </small>
                <div className="mt-2">
                  <img src="https://via.placeholder.com/60x20" alt="tamara" className="me-2" />
                  <img src="https://via.placeholder.com/60x20" alt="tabby" className="me-2" />
                </div>
              </div>

              {/* Delivery Information */}
              <div className="delivery-info p-3 mb-3">
                <div className="mb-2">
                  <strong>Delivery by Mon, 21st Jul</strong>
                </div>
                <div className="mb-2">
                  <small className="text-muted">When you order in 6 hours 20 mins</small>
                </div>
                <div>
                  <small className="text-muted">Dubai</small>
                </div>
              </div>

              {/* Size Selection */}
              <div className="size-selection mb-4">
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h6 className="mb-0 fw-bold">Pick your size</h6>
                  <a href="#" className="text-decoration-none">Size Guide ⌄</a>
                </div>

                <div className="size-options">
                  {sizes.map((sizeOption) => (
                    <button
                      key={sizeOption.size}
                      className={`btn size-button me-2 mb-2 ${
                        selectedSize === sizeOption.size 
                          ? 'btn-dark' 
                          : 'btn-outline-dark'
                      }`}
                      onClick={() => setSelectedSize(sizeOption.size)}
                      disabled={sizeOption.stock === 0}
                    >
                      {sizeOption.size}
                      {sizeOption.stock > 0 && sizeOption.stock <= 5 && (
                        <small className="d-block text-muted">
                          {sizeOption.stock} left
                        </small>
                      )}
                    </button>
                  ))}
                </div>

                {sizeError && (
                  <div className="size-error">
                    <i className="fas fa-exclamation-circle me-2"></i>
                    {sizeError}
                  </div>
                )}
              </div>

              {/* Add to Cart Section */}
              <div className="add-to-cart-section mb-4">
                <Row>
                  <Col xs={8}>
                    {isInCart ? (
                      <Button
                        variant="success"
                        size="lg"
                        className="w-100 add-to-bag-btn"
                        onClick={handleViewInBag}
                      >
                        <i className="fas fa-shopping-bag me-2"></i>
                        View in Bag ({cartItem?.quantity || 1})
                      </Button>
                    ) : (
                      <Button
                        variant="dark"
                        size="lg"
                        className="w-100 add-to-bag-btn"
                        onClick={handleAddToCart}
                        disabled={!selectedSize}
                      >
                        Add to Bag
                      </Button>
                    )}
                  </Col>
                  <Col xs={4}>
                    <Button
                      variant="outline-secondary"
                      size="lg"
                      className="w-100"
                      style={{ height: '48px' }}
                    >
                      <i className="fas fa-heart"></i>
                    </Button>
                  </Col>
                </Row>
              </div>

              {/* Product Information */}
              <div className="product-info-section">
                <h6 className="mb-3 fw-bold">Product Information</h6>
                <div className="row mb-2">
                  <div className="col-4 text-muted">Product Code:</div>
                  <div className="col-8">{product.id}</div>
                </div>
                <div className="row mb-2">
                  <div className="col-4 text-muted">Brand Name:</div>
                  <div className="col-8">{product.brand || 'FAV'}</div>
                </div>
                <div className="row mb-2">
                  <div className="col-4 text-muted">Multipack:</div>
                  <div className="col-8">Single</div>
                </div>
                <div className="row mb-2">
                  <div className="col-4 text-muted">Sleeve Length:</div>
                  <div className="col-8">{product.attributes?.sleeveLength || 'Short Sleeves'}</div>
                </div>
                <div className="row mb-2">
                  <div className="col-4 text-muted">Neck:</div>
                  <div className="col-8">{product.attributes?.neck || 'Collared Neck'}</div>
                </div>
                <a href="#" className="text-decoration-none">Show More ✓</a>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ProductDetailComponent;
