import React from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const FooterComponent = ({ config, data }) => {
  const {
    links = [],
    socialMedia = [],
    newsletter = true
  } = data;

  return (
    <footer className="bg-dark text-white py-5 mt-5">
      <Container>
        <Row>
          {links.map((linkGroup, index) => (
            <Col key={index} md={3} className="mb-4">
              <h6 className="mb-3 fw-bold">{linkGroup.title}</h6>
              <ul className="list-unstyled">
                {linkGroup.links.map((link, linkIndex) => (
                  <li key={linkIndex} className="mb-2">
                    <a href="#" className="text-muted text-decoration-none">
                      {link}
                    </a>
                  </li>
                ))}
              </ul>
            </Col>
          ))}

          {newsletter && (
            <Col md={3} className="mb-4">
              <h6 className="mb-3 fw-bold">Newsletter</h6>
              <p className="text-muted small mb-3">
                Subscribe to get updates on new arrivals and special offers.
              </p>
              <Form className="d-flex">
                <Form.Control
                  type="email"
                  placeholder="Enter your email"
                  className="me-2"
                />
                <Button variant="primary" size="sm">
                  Subscribe
                </Button>
              </Form>
            </Col>
          )}
        </Row>

        <hr className="my-4" />

        <Row className="align-items-center">
          <Col md={6}>
            <p className="mb-0 text-muted">
              Â© 2024 Styli. All rights reserved.
            </p>
          </Col>
          <Col md={6} className="text-md-end">
            <div className="social-links">
              {socialMedia.map((social, index) => (
                <a
                  key={index}
                  href={social.link}
                  className="text-muted text-decoration-none me-3"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <i className={`fab fa-${social.platform}`}></i>
                </a>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </footer>
  );
};

export default FooterComponent;
