import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  const [imageError, setImageError] = useState(false);

  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  // Handle different image field formats
  const getProductImage = (product) => {
    if (imageError) {
      return 'https://via.placeholder.com/300x400?text=Product+Image';
    }
    if (product.image) return product.image;
    if (product.images && product.images.length > 0) return product.images[0];
    return 'https://via.placeholder.com/300x400?text=Product+Image';
  };

  const handleImageError = () => {
    setImageError(true);
  };

  const hasDiscount = product.salePrice && product.salePrice < product.price;
  const discountPercentage = hasDiscount 
    ? Math.round(((product.price - product.salePrice) / product.price) * 100)
    : 0;

  return (
    <div className="card product-card h-100">
      <Link to={`/product/${product.id || product._id}`} className="text-decoration-none">
        <div className="position-relative">
          <img
            src={getProductImage(product)}
            alt={product.name}
            className="card-img-top"
            onError={handleImageError}
            loading="lazy"
          />
          {hasDiscount && (
            <div className="position-absolute top-0 start-0 m-2">
              <span className="badge bg-danger">
                -{discountPercentage}%
              </span>
            </div>
          )}
          {product.stock <= 5 && product.stock > 0 && (
            <div className="position-absolute top-0 end-0 m-2">
              <span className="badge bg-warning text-dark">
                Only {product.stock} left
              </span>
            </div>
          )}
          {product.stock === 0 && (
            <div className="position-absolute top-0 end-0 m-2">
              <span className="badge bg-secondary">
                Out of Stock
              </span>
            </div>
          )}
        </div>
      </Link>

      <div className="card-body d-flex flex-column">
        <div className="mb-2">
          <small className="text-muted text-uppercase">{product.brand}</small>
        </div>

        <Link to={`/product/${product.id || product._id}`} className="text-decoration-none">
          <h6 className="card-title text-dark mb-2">{product.name}</h6>
        </Link>

        <div className="mt-auto">
          <div className="d-flex align-items-center gap-2 mb-2">
            {hasDiscount ? (
              <>
                <span className="fw-bold text-dark">{formatCurrency(product.salePrice)}</span>
                <span className="text-muted text-decoration-line-through">{formatCurrency(product.price)}</span>
              </>
            ) : (
              <span className="fw-bold text-dark">{formatCurrency(product.price)}</span>
            )}
          </div>

          {product.stock > 0 && (
            <button className="btn btn-primary btn-sm w-100">
              Add to Cart
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
