import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  const formatCurrency = (amount) => {
    return `AED ${parseFloat(amount).toFixed(2)}`;
  };

  const renderPrice = () => {
    if (product.salePrice && product.salePrice < product.price) {
      const discount = Math.round(((product.price - product.salePrice) / product.price) * 100);
      return (
        <div className="d-flex align-items-center">
          <span className="fw-bold me-2">{formatCurrency(product.salePrice)}</span>
          <span className="text-muted text-decoration-line-through me-2">
            {formatCurrency(product.price)}
          </span>
          <span className="badge bg-success">{discount}% OFF</span>
        </div>
      );
    }
    return <span className="fw-bold">{formatCurrency(product.price)}</span>;
  };

  return (
    <div className="card h-100 product-card">
      <Link to={`/product/${product._id}`} className="text-decoration-none">
        <img
          src={product.images?.[0] || 'https://via.placeholder.com/300x400'}
          className="card-img-top"
          alt={product.name}
          style={{ height: '300px', objectFit: 'cover' }}
        />
        <div className="card-body">
          <h6 className="card-title text-dark mb-2">{product.name}</h6>
          <p className="text-muted small mb-2">{product.brand}</p>
          {renderPrice()}
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
