import React from 'react';
import { Link } from 'react-router-dom';

const HeaderComponent = ({ config, data }) => {
  const {
    logo = '/logo.png',
    showSearch = true,
    showCart = true,
    showUserMenu = true,
    navigation = [
      { label: 'Home', link: '/' },
      { label: 'Men', link: '/men' },
      { label: 'Women', link: '/women' },
      { label: 'Sale', link: '/sale' }
    ]
  } = config;

  return (
    <header className="bg-white shadow-sm border-bottom">
      <div className="container">
        <div className="d-flex justify-content-between align-items-center py-3">
          <Link to="/" className="text-decoration-none">
            <h1 className="h3 mb-0 text-dark">Styli</h1>
          </Link>

          <nav className="d-none d-md-flex">
            {navigation.map((item, index) => (
              <Link 
                key={index} 
                to={item.link} 
                className="text-decoration-none text-dark me-4"
              >
                {item.label}
              </Link>
            ))}
          </nav>

          <div className="d-flex align-items-center">
            {showSearch && (
              <div className="me-3">
                <input
                  type="text"
                  className="form-control form-control-sm"
                  placeholder="Search..."
                />
              </div>
            )}

            {showCart && (
              <Link to="/cart" className="btn btn-outline-dark me-2">
                <i className="fas fa-shopping-bag"></i>
              </Link>
            )}

            {showUserMenu && (
              <div className="dropdown">
                <button className="btn btn-outline-dark dropdown-toggle" type="button" data-bs-toggle="dropdown">
                  <i className="fas fa-user"></i>
                </button>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#">Profile</a></li>
                  <li><a className="dropdown-item" href="#">Orders</a></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item" href="#">Logout</a></li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default HeaderComponent;
