import { useState, useEffect, useCallback, useRef } from 'react';

// API base URL - in production, this would come from environment variables
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

const useServerDrivenUI = (pageType, pageId, userContext = {}) => {
  const [layout, setLayout] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastFetched, setLastFetched] = useState(null);

  // Use ref to store userContext to avoid dependency issues
  const userContextRef = useRef(userContext);
  userContextRef.current = userContext;

  // Flag to prevent multiple simultaneous calls
  const isFetchingRef = useRef(false);

  const fetchLayout = useCallback(async () => {
    // Prevent multiple simultaneous calls
    if (isFetchingRef.current) {
      console.log('ðŸ”„ Already fetching, skipping...');
      return;
    }

    try {
      isFetchingRef.current = true;
      console.log('ðŸ”„ Fetching layout:', { pageType, pageId, userContext: userContextRef.current });
      setLoading(true);
      setError(null);

      const queryParams = new URLSearchParams({
        ...userContextRef.current,
        timestamp: Date.now() // Cache busting
      });

      const apiUrl = `${API_BASE_URL}/api/layouts/${pageType}/${pageId}?${queryParams}`;
      console.log('ðŸ“¡ API URL:', apiUrl);

      // Use the full backend URL since React app is on port 3001 and backend is on port 3000
      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      });
      console.log('ðŸ“¥ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('ðŸ“¦ Response data:', data);

      if (data.success) {
        setLayout(data.layout);
        setLastFetched(Date.now());
        console.log('âœ… Layout loaded successfully');
      } else {
        setError(data.message || 'Failed to load layout');
        console.error('âŒ Layout load failed:', data.message);
      }
    } catch (error) {
      console.error('ðŸ’¥ Error fetching layout:', error);
      setError(`Failed to load page layout: ${error.message}`);
    } finally {
      setLoading(false);
      isFetchingRef.current = false;
    }
  }, [pageType, pageId]); // Removed userContext from dependencies

  const refreshLayout = useCallback(() => {
    console.log('ðŸ”„ Refreshing layout...');
    fetchLayout();
  }, [fetchLayout]);

  const updateComponentData = useCallback((componentId, newData) => {
    if (!layout) return;

    setLayout(prevLayout => ({
      ...prevLayout,
      components: prevLayout.components.map(component =>
        component.id === componentId
          ? { ...component, data: { ...component.data, ...newData } }
          : component
      )
    }));
  }, [layout]);

  const getComponentById = useCallback((componentId) => {
    if (!layout) return null;
    return layout.components.find(component => component.id === componentId);
  }, [layout]);

  const getComponentsByType = useCallback((componentType) => {
    if (!layout) return [];
    return layout.components.filter(component => component.type === componentType);
  }, [layout]);

  useEffect(() => {
    console.log('ðŸŽ¯ useServerDrivenUI effect triggered:', { pageType, pageId });
    fetchLayout();
  }, [fetchLayout]);

  return {
    layout,
    loading,
    error,
    lastFetched,
    refreshLayout,
    updateComponentData,
    getComponentById,
    getComponentsByType
  };
};

export default useServerDrivenUI;
